"use client";

import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import UsersTable from "@/components/admin/UsersTable";
import UserDetailsModal from "@/components/admin/UserDetailsModal";

export default function AdminUsersPage() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');

  const fetchUsers = async () => {
    if (!user) return;

    setLoading(true);
    try {
      const token = await user.getIdToken();

      const params = new URLSearchParams();
      if (roleFilter !== 'all') params.append('role', roleFilter);
      if (searchTerm) params.append('search', searchTerm);

      const response = await fetch(`/api/admin/users?${params.toString()}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }

      const data = await response.json();
      setUsers(data.data || []);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      const timeoutId = setTimeout(() => {
        fetchUsers();
      }, 300);

      return () => clearTimeout(timeoutId);
    }
  }, [user, roleFilter, searchTerm]);

  const handleSelectUser = (selectedUser) => {
    setSelectedUser(selectedUser);
  };

  const handleCloseModal = () => {
    setSelectedUser(null);
  };

  const handleUserUpdate = () => {
    setSelectedUser(null);
    fetchUsers();
  };

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Search & Filter Users</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-2">
              Search
            </label>
            <input
              id="search"
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search by name, email, or username..."
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-emerald-500 text-gray-900 bg-white"
            />
          </div>

          <div>
            <label htmlFor="role-filter" className="block text-sm font-medium text-gray-700 mb-2">
              Role
            </label>
            <select
              id="role-filter"
              value={roleFilter}
              onChange={(e) => setRoleFilter(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-emerald-500 text-gray-900 bg-white"
            >
              <option value="all" className="text-gray-900">All Roles</option>
              <option value="user" className="text-gray-900">Users</option>
              <option value="admin" className="text-gray-900">Admins</option>
            </select>
          </div>
        </div>
      </div>

      <UsersTable
        users={users}
        loading={loading}
        onSelectUser={handleSelectUser}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
      />

      {selectedUser && (
        <UserDetailsModal
          user={selectedUser}
          onClose={handleCloseModal}
          onUpdate={handleUserUpdate}
        />
      )}
    </div>
  );
}

src/components/ProfilePageWrapper.js.content:
'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { useRouter } from 'next/navigation';
import { useFCM } from '../hooks/useFCM';
import ProfilePage from './ProfilePage';
import NotificationBanner from './notifications/NotificationBanner';

function ProfilePageWrapper({ isOwnProfile = false }) {
  const { user, loading } = useAuth();
  const router = useRouter();
  const { notificationPermission } = useFCM();
  const [bannerDismissed, setBannerDismissed] = useState(false);

  useEffect(() => {
    const dismissed = localStorage.getItem('notification-banner-dismissed');
    setBannerDismissed(!!dismissed);
  }, []);

  const handleDismissBanner = () => {
    localStorage.setItem('notification-banner-dismissed', 'true');
    setBannerDismissed(true);
  };


  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  // If it's supposed to be own profile but user is not authenticated
  if (isOwnProfile && !user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="max-w-md mx-auto text-center px-6">
          <div className="mx-auto w-24 h-24 bg-emerald-100 rounded-full flex items-center justify-center mb-6">
            <svg className="w-12 h-12 text-emerald-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
            </svg>
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-2">You haven't created an account</h3>
          <p className="text-gray-600 mb-6">Create an account or sign in if you already have one to view and manage your profile.</p>
          <div className="space-y-3">
            <button 
              onClick={() => router.push('/signup')}
              className="btn-base btn-primary w-full px-6 py-3 font-medium"
            >
              Create Account
            </button>
            <button 
              onClick={() => router.push('/signin')}
              className="btn-base btn-secondary w-full px-6 py-3 font-medium"
            >
              Sign In
            </button>
          </div>
        </div>
      </div>
    );
  }

  // User is authenticated or viewing someone else's profile
  return (
    <>
      {isOwnProfile && user && notificationPermission === 'default' && !bannerDismissed && (
        <NotificationBanner onDismiss={handleDismissBanner} />
      )}
      <ProfilePage isOwnProfile={isOwnProfile} />
    </>
  );
}

ProfilePageWrapper.displayName = 'ProfilePageWrapper';
export default ProfilePageWrapper;

