"use client";

import { useEffect, useState } from 'react';

export default function FilterModal({ 
  isOpen, 
  onClose, 
  onApply,
  initialFilters,
  filterFields
}) {
  const [tempFilters, setTempFilters] = useState(initialFilters);
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    if (isOpen) {
      setTempFilters(initialFilters);
      setHasChanges(false);
    }
  }, [isOpen, initialFilters]);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  useEffect(() => {
    const changed = JSON.stringify(tempFilters) !== JSON.stringify(initialFilters);
    setHasChanges(changed);
  }, [tempFilters, initialFilters]);

  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleFilterChange = (key, value) => {
    setTempFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleApply = () => {
    onApply(tempFilters);
    onClose();
  };

  const handleCancel = () => {
    setTempFilters(initialFilters);
    onClose();
  };

  return (
    <div 
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
      onClick={handleBackdropClick}
    >
      <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full mx-4 transform transition-all">
        {/* Header - Yellow background */}
        <div className="bg-yellow-400 px-6 py-6 relative rounded-t-xl">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-emerald-700">
              Filter Options
            </h2>
            <p className="text-base text-gray-700 mt-1">
              Customize your search criteria
            </p>
          </div>
          <button
            onClick={handleCancel}
            className="absolute top-4 right-4 btn-base p-2 hover:bg-yellow-500 rounded-full transition-colors"
          >
            <svg className="w-5 h-5 text-gray-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Body - Filter Fields */}
        <div className="bg-white border-t-0 border-gray-200 px-6 py-8 rounded-b-xl">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {filterFields.map((field) => (
              <div key={field.key}>
                <label className="block text-sm font-medium text-gray-800 mb-2">
                  {field.label}
                </label>
                <select
                  value={tempFilters[field.key] || ''}
                  onChange={(e) => handleFilterChange(field.key, e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500 outline-none transition-all duration-300 text-gray-900 text-sm"
                >
                  {field.options.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>
            ))}
          </div>

          {/* Footer - Action Buttons */}
          <div className="mt-8 flex gap-3 justify-end">
            <button
              onClick={handleCancel}
              className="btn-base btn-secondary px-6 py-2 text-sm"
            >
              Cancel
            </button>
            <button
              onClick={handleApply}
              disabled={!hasChanges}
              className={`btn-base px-6 py-2 text-sm ${
                hasChanges 
                  ? 'btn-primary' 
                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
            >
              Apply Filters
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

src/lib/firebase-optimized.js.content:
"use client";

// Optimized Firebase configuration with static imports for better performance
import { initializeApp, getApps, getApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore";
import { getMessaging, isSupported } from "firebase/messaging";
import { useState, useEffect } from 'react';

// Module-level Firebase instances (initialized immediately on client)
let firebaseApp = null;
let firebaseAuth = null;
let firebaseDb = null;
let firebaseMessaging = null;
let isInitialized = false;
let isConfigured = false;
let messagingSupported = false;

// Initialize Firebase immediately at module load (client-side only)
const initializeFirebaseModule = () => {
  // Only run on client
  if (typeof window === 'undefined') {
    return;
  }

  // Only initialize once
  if (isInitialized) {
    return;
  }

  try {
    // Check environment variables
    const apiKey = process.env.NEXT_PUBLIC_FIREBASE_API_KEY;
    const projectId = process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID;
    const appId = process.env.NEXT_PUBLIC_FIREBASE_APP_ID;
    const messagingSenderId = process.env.NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID;

    // Check if Firebase should be disabled
    if (!apiKey || !projectId || !appId || 
        apiKey === 'not needed' || projectId === 'not needed' || appId === 'not needed' ||
        apiKey === '' || projectId === '' || appId === '') {
      console.log('Firebase disabled - no valid configuration found');
      isInitialized = true;
      isConfigured = false;
      return;
    }

    const firebaseConfig = {
      apiKey,
      authDomain: `${projectId}.firebaseapp.com`,
      projectId,
      storageBucket: `${projectId}.appspot.com`,
      messagingSenderId,
      appId,
    };

    // Initialize Firebase - prevent duplicate initialization
    firebaseApp = getApps().length ? getApp() : initializeApp(firebaseConfig);
    firebaseAuth = getAuth(firebaseApp);
    firebaseDb = getFirestore(firebaseApp);

    // Initialize Firebase Cloud Messaging if supported
    isSupported().then((supported) => {
      if (supported) {
        firebaseMessaging = getMessaging(firebaseApp);
        messagingSupported = true;
      } else {
        console.log('Firebase Messaging not supported in this browser');
        messagingSupported = false;
      }
    }).catch((error) => {
      console.error('Error checking FCM support:', error);
      messagingSupported = false;
    });

    isInitialized = true;
    isConfigured = true;
  } catch (error) {
    console.error('Firebase initialization failed:', error);
    isInitialized = true;
    isConfigured = false;
  }
};

// Initialize Firebase immediately when module loads (client-side only)
initializeFirebaseModule();

// Hook to access Firebase instances in React components
export const useFirebaseOptimized = () => {
  const [firebase, setFirebase] = useState({
    app: firebaseApp,
    auth: firebaseAuth,
    db: firebaseDb,
    messaging: firebaseMessaging,
    isLoading: false,
    isConfigured: isConfigured,
    messagingSupported: messagingSupported
  });

  useEffect(() => {
    // Ensure Firebase is initialized (should already be done at module load)
    initializeFirebaseModule();

    // Update state with initialized instances
    setFirebase({
      app: firebaseApp,
      auth: firebaseAuth,
      db: firebaseDb,
      messaging: firebaseMessaging,
      isLoading: false,
      isConfigured: isConfigured,
      messagingSupported: messagingSupported
    });
  }, []);

  return firebase;
};

// Export Firebase services (now available immediately at module load)
export { firebaseAuth as auth, firebaseDb as db, firebaseMessaging as messaging };
export default firebaseApp;

src/app/api/storage/signed-url/route.js.content:
import { NextResponse } from 'next/server'
import { verifyIdToken } from '../../../../lib/firebaseAdmin'
import { supabaseAdmin } from '../../../../lib/supabase-admin'
import { headers } from 'next/headers'

// Force Node.js runtime for server-side operations
export const runtime = 'nodejs'

export async function POST(request) {
  try {
    // Get the authorization header
    const headersList = headers()
    const authorization = headersList.get('authorization')

    if (!authorization || !authorization.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'No authorization token provided' }, { status: 401 })
    }

    const token = authorization.replace('Bearer ', '')

    // Verify Firebase ID token
    let decodedToken
    try {
      decodedToken = await verifyIdToken(token)
    } catch (error) {
      return NextResponse.json({ error: 'Invalid token' }, { status: 401 })
    }

    const { path, expiresIn = 3600 } = await request.json()

    if (!path) {
      return NextResponse.json({ error: 'path is required' }, { status: 400 })
    }

    // Verify user owns this file - handle different folder structures
    const allowedPrefixes = [
      `uploads/${decodedToken.uid}/`,
      `profile-images/${decodedToken.uid}/`,
      `documents/${decodedToken.uid}/`,
      `temp/${decodedToken.uid}/`,
      `campaigns/${decodedToken.uid}/`
    ]

    const hasValidPrefix = allowedPrefixes.some(prefix => path.startsWith(prefix))
    if (!hasValidPrefix) {
      return NextResponse.json({ error: 'Access denied' }, { status: 403 })
    }

    // Generate signed download URL
    const { data, error } = await supabaseAdmin.storage
      .from('uploads')
      .createSignedUrl(path, expiresIn)

    if (error) {
      console.error('Supabase error:', error)
      return NextResponse.json({ error: 'Failed to create signed URL' }, { status: 500 })
    }

    return NextResponse.json({
      signedUrl: data.signedUrl
    })

  } catch (error) {
    console.error('Signed URL generation error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

