"use client";

import { usePathname } from 'next/navigation';
import { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import MobileMenu from './MobileMenu';
import { useLayoutVisibility } from './LayoutVisibilityContext';

// Pages where header/footer should NOT be shown
const EXCLUDED_PAGES = [
  '/signin',
  '/signup',
  '/forgot-password',
  '/onboarding',
  '/verify-email'
];

export default function ConditionalLayout({ children }) {
  const pathname = usePathname();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { hideChrome } = useLayoutVisibility();

  // Check if current page should exclude header/footer
  const shouldExcludeLayout = hideChrome || 
    EXCLUDED_PAGES.includes(pathname) || 
    pathname.startsWith('/admin');

  // If layout should be excluded, just return children
  if (shouldExcludeLayout) {
    return children;
  }

  // Show header/footer for all other pages
  return (
    <div className="min-h-screen flex flex-col relative">
      {/* Main Content with blur effect */}
      <div className={`min-h-screen flex flex-col transition-all duration-300 ${
        isMenuOpen ? 'blur-sm' : ''
      }`}>
        <Header isMenuOpen={isMenuOpen} setIsMenuOpen={setIsMenuOpen} />
        <main className="flex-1">
          {children}
        </main>
        <Footer />
      </div>

      {/* Mobile Menu Component */}
      <MobileMenu 
        isMenuOpen={isMenuOpen}
        setIsMenuOpen={setIsMenuOpen}
      />
    </div>
  );
}

src/app/api/campaigns/track-download/rou.content:
import { NextResponse } from 'next/server';
import { adminFirestore } from '../../../../lib/firebaseAdmin';
import { FieldValue } from 'firebase-admin/firestore';

export async function POST(request) {
  try {
    const body = await request.json();
    const { campaignId } = body;

    // Validate required field
    if (!campaignId) {
      return NextResponse.json(
        { success: false, error: 'Campaign ID is required' },
        { status: 400 }
      );
    }

    // Get Firestore instance
    const db = adminFirestore();

    // Run transaction to update campaign supports count
    const result = await db.runTransaction(async (transaction) => {
      // Get campaign document
      const campaignRef = db.collection('campaigns').doc(campaignId);
      const campaignDoc = await transaction.get(campaignRef);

      if (!campaignDoc.exists) {
        throw new Error('Campaign not found');
      }

      const campaignData = campaignDoc.data();

      // Create a download record in subcollection with timestamp
      const downloadRef = db.collection('campaigns').doc(campaignId).collection('downloads').doc();
      transaction.set(downloadRef, {
        downloadedAt: FieldValue.serverTimestamp(),
        createdAt: FieldValue.serverTimestamp()
      });

      // Update campaign supportersCount (tracks total downloads/supports)
      transaction.update(campaignRef, {
        supportersCount: FieldValue.increment(1),
        updatedAt: FieldValue.serverTimestamp(),
        // Set firstUsedAt on first download
        ...(campaignData.supportersCount === 0 ? { firstUsedAt: FieldValue.serverTimestamp() } : {})
      });

      return {
        success: true
      };
    });

    return NextResponse.json(result);
  } catch (error) {
    console.error('Error tracking download:', error);

    // Return appropriate error message
    if (error.message === 'Campaign not found') {
      return NextResponse.json(
        { success: false, error: 'Campaign not found' },
        { status: 404 }
      );
    }

    return NextResponse.json(
      { success: false, error: 'Failed to track download' },
      { status: 500 }
    );
  }
}

