src/app/layout.js.content:
import { Geist, Geist_Mono } from "next/font/google";
import Script from "next/script";
import { Suspense } from "react";
import "./globals.css";
import ClientAuthProvider from "../components/ClientAuthProvider";
import ErrorBoundary from "../components/ErrorBoundary";
import TimeoutWrapper from "../components/TimeoutWrapper";
import AuthenticatedLayout from "../components/AuthenticatedLayout";
import AuthGate from "../components/AuthGate";
import { CampaignSessionProvider } from "../contexts/CampaignSessionContext";
import NotificationProvider from "../components/notifications/NotificationProvider";
import Analytics from "./analytics";

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
});

export const metadata = {
  title: "Frame Your Voice - Twibbonize App",
  description: "Create and share frames that amplify your message, celebrate your cause, and inspire others to join in.",
};

export default function RootLayout({ children }) {
  const gaId = process.env.NEXT_PUBLIC_GA_MEASUREMENT_ID;

  return (
    <html lang="en" suppressHydrationWarning={true}>
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased`}
        suppressHydrationWarning={true}
      >
        {gaId && (
          <>
            <Script
              src={`https://www.googletagmanager.com/gtag/js?id=${gaId}`}
              strategy="afterInteractive"
            />
            <Script id="google-analytics" strategy="afterInteractive">
              {`
                window.dataLayer = window.dataLayer || [];
                function gtag(){dataLayer.push(arguments);}
                gtag('js', new Date());
                gtag('config', '${gaId}', {
                  page_path: window.location.pathname,
                });
              `}
            </Script>
          </>
        )}
        <ErrorBoundary>
          <TimeoutWrapper timeout={15000}>
            <ClientAuthProvider>
              <NotificationProvider>
                <CampaignSessionProvider>
                  <AuthenticatedLayout>
                    <AuthGate>
                      {children}
                    </AuthGate>
                  </AuthenticatedLayout>
                </CampaignSessionProvider>
              </NotificationProvider>
            </ClientAuthProvider>
          </TimeoutWrapper>
        </ErrorBoundary>
        {gaId && (
          <Suspense fallback={null}>
            <Analytics />
          </Suspense>
        )}
      </body>
    </html>
  );
}

src/app/(chrome)/campaign/[slug]/page.js.content:
"use client";

import { useState, useRef, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { getCampaignBySlug } from '../../../../lib/firestore';
import { useCampaignSession } from '../../../../contexts/CampaignSessionContext';
import { useAuth } from '../../../../hooks/useAuth';
import { getCampaignPreview, getProfileAvatar } from '../../../../utils/imageTransform';
import LoadingSpinner from '../../../../components/LoadingSpinner';
import CampaignStepIndicator from '../../../../components/CampaignStepIndicator';

export default function CampaignUploadPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useAuth();
  const slug = params.slug;
  const campaignSession = useCampaignSession();

  // State
  const [campaign, setCampaign] = useState(null);
  const [creator, setCreator] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notFound, setNotFound] = useState(false);
  const [uploading, setUploading] = useState(false);

  // Report modal state
  const [showReportModal, setShowReportModal] = useState(false);
  const [reportReason, setReportReason] = useState('');
  const [reportDetails, setReportDetails] = useState('');
  const [reportSubmitting, setReportSubmitting] = useState(false);
  const [reportSuccess, setReportSuccess] = useState(false);
  const [error, setError] = useState('');

  // Lock body scroll when modal is open
  useEffect(() => {
    if (showReportModal) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [showReportModal]);

  const fileInputRef = useRef();

  // Fetch campaign data
  useEffect(() => {
    const fetchCampaign = async () => {
      if (!slug) return;

      setLoading(true);
      try {
        const result = await getCampaignBySlug(slug);

        if (!result) {
          setNotFound(true);
          setLoading(false);
          return;
        }

        setCampaign(result.campaign);
        setCreator(result.creator);

        // Store campaign and creator data in session
        campaignSession.setCampaignData(slug, result.campaign);
        campaignSession.setCreatorData(slug, result.creator);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching campaign:', error);
        setNotFound(true);
        setLoading(false);
      }
    };

    fetchCampaign();
  }, [slug]);

  // Handle photo selection
  const handlePhotoSelect = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setError('');
    setUploading(true);

    // File size validation (10MB limit)
    const maxSize = 10 * 1024 * 1024;
    if (file.size > maxSize) {
      setError(`Photo must be smaller than 10MB. Current size: ${(file.size / (1024 * 1024)).toFixed(1)}MB`);
      setUploading(false);
      return;
    }

    // File type validation
    const validTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/webp'];
    if (!validTypes.includes(file.type)) {
      setError('Please upload a PNG, JPG, or WEBP image');
      setUploading(false);
      return;
    }

    // Create preview
    const reader = new FileReader();
    reader.onload = async (event) => {
      // Store in session
      campaignSession.setUserPhoto(slug, file, event.target.result);

      // Initialize adjustments
      campaignSession.setAdjustments(slug, { scale: 1.0, x: 0, y: 0 });

      // Redirect to adjust page
      router.push(`/campaign/${slug}/adjust`);
    };

    reader.onerror = () => {
      setError('Failed to read image file');
      setUploading(false);
    };

    reader.readAsDataURL(file);
  };

  // Report campaign
  const handleReportSubmit = async (e) => {
    e.preventDefault();

    if (!reportReason) {
      setError('Please select a reason for reporting');
      return;
    }

    setReportSubmitting(true);
    setError('');

    try {
      const reportData = {
        campaignId: campaign.id,
        campaignSlug: campaign.slug,
        reportedBy: user?.uid || 'anonymous',
        reason: reportReason,
        details: reportDetails
      };

      const response = await fetch('/api/reports/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reportData),
      });

      const result = await response.json();

      if (result.success) {
        setReportSuccess(true);
      } else {
        setError(result.error || 'Failed to submit report');
      }

      setReportSubmitting(false);
    } catch (error) {
      console.error('Error submitting report:', error);
      setError('Failed to submit report. Please try again.');
      setReportSubmitting(false);
    }
  };

  // Share functionality
  const handleShare = async (platform) => {
    const url = window.location.href;
    const text = campaign.title + (campaign.description ? ` - ${campaign.description}` : '');

    switch (platform) {
      case 'twitter':
        window.open(`https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(url)}`, '_blank');
        break;
      case 'facebook':
        window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`, '_blank');
        break;
      case 'whatsapp':
        window.open(`https://wa.me/?text=${encodeURIComponent(text + ' ' + url)}`, '_blank');
        break;
      default:
        break;
    }
  };

  // Loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  // Not found state
  if (notFound || !campaign) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center px-4">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Campaign Not Found</h1>
          <p className="text-gray-600 mb-8">This campaign doesn't exist or has been removed.</p>
          <button
            onClick={() => router.push('/')}
            className="btn-base btn-primary px-6 py-3 font-medium"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="min-h-screen flex">
        {/* Main Content */}
        <div className="flex-1 w-full flex flex-col py-8 px-4 sm:px-6 lg:px-8 pt-20">
          <div className="mx-auto w-full max-w-5xl">

            {/* Header */}
            <div className="text-center mb-6 bg-yellow-400 px-6 py-5 rounded-t-xl">
              <CampaignStepIndicator currentStep={1} />
              <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-1">{campaign.title}</h1>
              {campaign.description && (
                <p className="text-sm text-gray-800 mt-1">{campaign.description}</p>
              )}
            </div>

            {/* Content Card */}
            <div className="bg-white rounded-b-xl border border-t-0 border-gray-200 shadow-sm">

              {error && (
                <div className="mx-4 mt-4 p-4 bg-red-50 border border-red-200 rounded-lg text-red-600 text-sm text-center">
                  {error}
                </div>
              )}

              <div className="grid grid-cols-1 lg:grid-cols-[minmax(0,1.1fr)_minmax(320px,0.9fr)] gap-6 p-4 sm:p-6">

                {/* Left: Campaign Preview */}
                <div className="space-y-3">
                  <h2 className="text-lg font-bold text-gray-900">Campaign Preview</h2>

                  <div className="relative">
                    <img
                      src={getCampaignPreview(campaign.imageUrl)}
                      alt={campaign.title}
                      className="w-full h-auto rounded-lg border-2 border-gray-300"
                      style={{ maxHeight: '500px', objectFit: 'contain' }}
                    />
                    <div className="absolute top-4 right-4 bg-emerald-500 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase">
                      {campaign.type === 'frame' ? 'Frame' : 'Background'}
                    </div>
                  </div>

                  {creator && (
                    <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg border border-gray-200">
                      {creator.profileImage && (
                        <img
                          src={getProfileAvatar(creator.profileImage)}
                          alt={creator.displayName}
                          className="w-10 h-10 rounded-full border-2 border-gray-300"
                        />
                      )}
                      <div className="flex-1">
                        <p className="text-sm text-gray-600">Created by</p>
                        <button
                          onClick={() => router.push(`/u/${creator.username}`)}
                          className="font-semibold text-gray-900 hover:underline text-sm"
                        >
                          {creator.displayName || creator.username}
                        </button>
                        <p className="text-xs text-gray-600">
                          {campaign.supportersCount || 0} {campaign.supportersCount === 1 ? 'support' : 'supports'}
                        </p>
                      </div>
                    </div>
                  )}
                </div>

                {/* Right: Upload & Actions */}
                <div className="space-y-5">

                  {/* Upload Section */}
                  <div>
                    <h2 className="text-lg font-bold text-gray-900 mb-3">Add Your Photo</h2>
                    <p className="text-sm text-gray-600 mb-4">
                      Upload your photo to create your personalized {campaign.type === 'frame' ? 'frame' : 'background'}
                    </p>

                    <input
                      ref={fileInputRef}
                      type="file"
                      accept="image/png,image/jpeg,image/jpg,image/webp"
                      onChange={handlePhotoSelect}
                      disabled={uploading}
                      className="hidden"
                      id="user-photo-input"
                    />

                    <label
                      htmlFor="user-photo-input"
                      className={`btn-base btn-primary w-full text-center py-4 cursor-pointer font-bold text-lg ${
                        uploading ? 'opacity-70 cursor-wait' : ''
                      }`}
                    >
                      {uploading ? 'Loading...' : 'Choose Your Photo'}
                    </label>

                    <p className="text-xs text-gray-600 mt-2 text-center">
                      PNG, JPG, or WEBP (max 10MB)
                    </p>
                  </div>

                  {/* Share Campaign */}
                  <div className="pt-3 border-t border-gray-200">
                    <h3 className="text-lg font-bold text-gray-900 mb-3">Share Campaign</h3>
                    <div className="grid grid-cols-3 gap-3">
                      <button
                        onClick={() => handleShare('twitter')}
                        className="btn-base bg-blue-400 hover:bg-blue-500 text-white py-3 text-sm font-medium"
                      >
                        Twitter
                      </button>
                      <button
                        onClick={() => handleShare('facebook')}
                        className="btn-base bg-blue-600 hover:bg-blue-700 text-white py-3 text-sm font-medium"
                      >
                        Facebook
                      </button>
                      <button
                        onClick={() => handleShare('whatsapp')}
                        className="btn-base bg-green-500 hover:bg-green-600 text-white py-3 text-sm font-medium"
                      >
                        WhatsApp
                      </button>
                    </div>
                  </div>

                  {/* Report Button */}
                  <button
                    onClick={() => setShowReportModal(true)}
                    className="btn-base bg-red-100 hover:bg-red-200 text-red-700 w-full py-2 text-sm font-medium"
                  >
                    Report Campaign
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Report Modal */}
      {showReportModal && (
        <>
          {/* Backdrop with blur */}
          <div 
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 transition-opacity"
            onClick={() => {
              setShowReportModal(false);
              setReportReason('');
              setReportDetails('');
              setError('');
            }}
          />

          {/* Modal */}
          <div className="fixed inset-0 z-50 flex items-center justify-center p-4 pointer-events-none">
            <div 
              className="bg-white rounded-lg max-w-md w-full p-6 pointer-events-auto"
              onClick={(e) => e.stopPropagation()}
            >
              {reportSuccess ? (
                // Success State
                <div className="text-center py-4">
                  <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-green-100 mb-4">
                    <svg className="h-8 w-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                  </div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">Report Submitted</h3>
                  <p className="text-gray-600 mb-6">
                    Thank you for your report. We will review it shortly.
                  </p>
                  <button
                    onClick={() => {
                      setShowReportModal(false);
                      setReportReason('');
                      setReportDetails('');
                      setReportSuccess(false);
                      setError('');
                    }}
                    className="btn-base btn-primary px-8 py-2 font-medium"
                  >
                    OK
                  </button>
                </div>
              ) : (
                // Report Form
                <>
                  <h2 className="text-2xl font-bold text-gray-900 mb-4">Report Campaign</h2>

                  {error && (
                    <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg text-red-600 text-sm">
                      {error}
                    </div>
                  )}

                  <form onSubmit={handleReportSubmit}>
                    <div className="mb-4">
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Reason <span className="text-red-500">*</span>
                      </label>
                      <select
                        value={reportReason}
                        onChange={(e) => setReportReason(e.target.value)}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500 outline-none transition-all text-gray-900 bg-white"
                      >
                        <option value="" className="text-gray-400">Select a reason</option>
                        <option value="inappropriate" className="text-gray-900">Inappropriate Content</option>
                        <option value="spam" className="text-gray-900">Spam</option>
                        <option value="copyright" className="text-gray-900">Copyright Violation</option>
                        <option value="other" className="text-gray-900">Other</option>
                      </select>
                    </div>

                    <div className="mb-6">
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Additional Details (Optional)
                      </label>
                      <textarea
                        value={reportDetails}
                        onChange={(e) => setReportDetails(e.target.value)}
                        rows={3}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500 outline-none transition-all resize-none text-gray-900 bg-white"
                        placeholder="Provide more context about your report..."
                      />
                    </div>

                    <div className="flex gap-3">
                      <button
                        type="button"
                        onClick={() => {
                          setShowReportModal(false);
                          setReportReason('');
                          setReportDetails('');
                          setError('');
                        }}
                        className="btn-base btn-secondary flex-1 py-2 font-medium"
                        disabled={reportSubmitting}
                      >
                        Cancel
                      </button>
                      <button
                        type="submit"
                        className="btn-base bg-red-500 hover:bg-red-600 text-white flex-1 py-2 font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                        disabled={reportSubmitting || !reportReason}
                      >
                        {reportSubmitting ? 'Submitting...' : 'Submit Report'}
                      </button>
                    </div>
                  </form>
                </>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

src/components/admin/AdminActionButton.j.content:
"use client";

import { useState } from "react";

/**
 * Reusable action button with loading state and optional confirmation
 * 
 * @param {Object} props
 * @param {Function} props.onClick - Click handler (can be async)
 * @param {string} props.children - Button text
 * @param {string} props.variant - Button style: 'primary', 'danger', 'warning', 'success'
 * @param {boolean} props.requireConfirm - Show confirmation dialog
 * @param {string} props.confirmTitle - Confirmation dialog title
 * @param {string} props.confirmMessage - Confirmation dialog message
 * @param {string} props.confirmButtonText - Confirm button text
 * @param {string} props.loadingText - Text to show when loading
 * @param {string} props.icon - Optional icon element
 * @param {boolean} props.disabled - Disabled state
 * @param {string} props.size - Button size: 'sm', 'md', 'lg'
 * @param {string} props.className - Additional CSS classes
 */
export default function AdminActionButton({
  onClick,
  children,
  variant = 'primary',
  requireConfirm = false,
  confirmTitle = 'Confirm Action',
  confirmMessage = 'Are you sure you want to proceed?',
  confirmButtonText = 'Confirm',
  loadingText = 'Processing...',
  icon = null,
  disabled = false,
  size = 'md',
  className = '',
}) {
  const [isLoading, setIsLoading] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);
  const [error, setError] = useState(null);

  const getVariantClasses = () => {
    const baseClasses = 'btn-base transition-colors disabled:opacity-50 disabled:cursor-not-allowed';

    const sizeClasses = {
      sm: 'text-sm py-1 px-3',
      md: 'text-base py-2 px-4',
      lg: 'text-lg py-3 px-6',
    };

    const variantClasses = {
      primary: 'bg-emerald-600 text-white hover:bg-emerald-700',
      danger: 'bg-red-600 text-white hover:bg-red-700',
      warning: 'bg-yellow-600 text-white hover:bg-yellow-700',
      success: 'bg-green-600 text-white hover:bg-green-700',
      secondary: 'bg-gray-600 text-white hover:bg-gray-700',
    };

    return `${baseClasses} ${sizeClasses[size]} ${variantClasses[variant]} ${className}`;
  };

  const handleClick = async () => {
    if (requireConfirm) {
      setShowConfirm(true);
      return;
    }

    await executeAction();
  };

  const executeAction = async () => {
    setIsLoading(true);
    setError(null);

    try {
      await onClick();
      setShowConfirm(false);
    } catch (err) {
      console.error('Action error:', err);
      setError(err.message || 'An error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const handleConfirm = async () => {
    await executeAction();
  };

  const handleCancel = () => {
    setShowConfirm(false);
    setError(null);
  };

  return (
    <>
      <button
        onClick={handleClick}
        disabled={disabled || isLoading}
        className={getVariantClasses()}
      >
        {isLoading ? (
          <span className="flex items-center gap-2">
            <svg className="animate-spin h-4 w-4" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
            </svg>
            {loadingText}
          </span>
        ) : (
          <span className="flex items-center gap-2">
            {icon}
            {children}
          </span>
        )}
      </button>

      {error && (
        <div className="mt-2 text-sm text-red-600 bg-red-50 p-2 rounded">
          {error}
        </div>
      )}

      {showConfirm && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              {confirmTitle}
            </h3>
            <p className="text-sm text-gray-600 mb-6">
              {confirmMessage}
            </p>

            {error && (
              <div className="mb-4 text-sm text-red-600 bg-red-50 p-2 rounded">
                {error}
              </div>
            )}

            <div className="flex gap-3">
              <button
                onClick={handleCancel}
                disabled={isLoading}
                className="flex-1 btn-base bg-gray-600 text-white hover:bg-gray-700 disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                onClick={handleConfirm}
                disabled={isLoading}
                className={`flex-1 btn-base ${
                  variant === 'danger' 
                    ? 'bg-red-600 hover:bg-red-700' 
                    : 'bg-emerald-600 hover:bg-emerald-700'
                } text-white disabled:opacity-50`}
              >
                {isLoading ? loadingText : confirmButtonText}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

