import { NextResponse } from 'next/server'
import { verifyIdToken } from '../../../../lib/firebaseAdmin'
import { supabaseAdmin } from '../../../../lib/supabase-admin'
import { headers } from 'next/headers'

// Force Node.js runtime for server-side operations
export const runtime = 'nodejs'

export async function POST(request) {
  try {
    // Get the authorization header
    const headersList = headers()
    const authorization = headersList.get('authorization')

    if (!authorization || !authorization.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'No authorization token provided' }, { status: 401 })
    }

    const token = authorization.replace('Bearer ', '')

    // Verify Firebase ID token
    let decodedToken
    try {
      decodedToken = await verifyIdToken(token)
    } catch (error) {
      return NextResponse.json({ error: 'Invalid token' }, { status: 401 })
    }

    const { fileName, fileType, fileSize, folder = 'uploads' } = await request.json()

    // Validate folder (whitelist allowed folders)
    const allowedFolders = ['uploads', 'profile-images', 'documents', 'temp', 'campaigns']
    if (!allowedFolders.includes(folder)) {
      return NextResponse.json({ error: 'Invalid folder' }, { status: 400 })
    }

    // Validate required fields
    if (!fileName) {
      return NextResponse.json({ error: 'fileName is required' }, { status: 400 })
    }

    if (!Number.isFinite(fileSize) || fileSize <= 0) {
      return NextResponse.json({ 
        error: 'fileSize is required and must be a positive number' 
      }, { status: 400 })
    }

    if (!fileType || typeof fileType !== 'string' || fileType.trim() === '') {
      return NextResponse.json({ 
        error: 'fileType is required and must be a non-empty string' 
      }, { status: 400 })
    }

    // Validate file size (5MB max for campaigns and profile-images, 10MB max for others)
    const maxFileSize = (folder === 'campaigns' || folder === 'profile-images') ? 5 * 1024 * 1024 : 10 * 1024 * 1024
    if (fileSize > maxFileSize) {
      const maxSizeMB = (folder === 'campaigns' || folder === 'profile-images') ? 5 : 10
      return NextResponse.json({ 
        error: `File size exceeds ${maxSizeMB}MB limit` 
      }, { status: 400 })
    }

    // Validate file type for campaigns
    if (folder === 'campaigns') {
      const allowedTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/webp']
      if (!allowedTypes.includes(fileType.toLowerCase())) {
        return NextResponse.json({ 
          error: 'Invalid file type for campaigns. Only PNG, JPG, and WEBP are allowed.' 
        }, { status: 400 })
      }
    }

    // Create user-specific path
    const timestamp = Date.now()
    const sanitizedFileName = fileName.replace(/[^a-zA-Z0-9.-]/g, '_')
    const filePath = `${folder}/${decodedToken.uid}/${timestamp}-${sanitizedFileName}`

    // Generate signed upload URL (valid for 1 hour)
    const { data, error } = await supabaseAdmin.storage
      .from('uploads')
      .createSignedUploadUrl(filePath)

    if (error) {
      console.error('Supabase error:', error)
      return NextResponse.json({ error: 'Failed to create upload URL' }, { status: 500 })
    }

    return NextResponse.json({
      uploadUrl: data.signedUrl,
      path: filePath,
      token: data.token
    })

  } catch (error) {
    console.error('Upload URL generation error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

src/components/UserOnboardingWrapper.js.content:
'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '../hooks/useAuth';
import { getUserProfile } from '../lib/firestore';

export default function UserOnboardingWrapper({ children }) {
  const { user, loading } = useAuth();
  const router = useRouter();
  const [checkingProfile, setCheckingProfile] = useState(false);

  useEffect(() => {
    const checkProfileStatus = async () => {
      if (!user || loading) return;

      // For email/password users, check if email is verified first
      if (user.providerData[0]?.providerId === 'password' && !user.emailVerified) {
        return;
      }

      setCheckingProfile(true);

      // Add a small delay to ensure Firebase auth state has stabilized after verification
      await new Promise(resolve => setTimeout(resolve, 1000));

      try {
        let userProfile = await getUserProfile(user.uid);

        // If profile doesn't exist, wait a bit and try again (profile creation might be in progress)
        if (!userProfile) {
          await new Promise(resolve => setTimeout(resolve, 2000));
          userProfile = await getUserProfile(user.uid);
        }

        // If still no profile, this is unexpected
        if (!userProfile) {
          return;
        }


        // Navigate to onboarding page if user exists but hasn't completed profile setup
        if (userProfile && !userProfile.profileCompleted) {
          router.push('/onboarding');
        }
      } catch (error) {
      } finally {
        setCheckingProfile(false);
      }
    };

    checkProfileStatus();
  }, [user, loading]);


  // Don't render welcome popup if still loading or checking
  if (loading || checkingProfile) {
    return children;
  }

  return children;
}

src/lib/firestore.js.content:
"use client";

// Firestore database operations for the Twibbonize app
import { db } from './firebase-optimized';
import { handleFirebaseError } from '../utils/firebaseErrorHandler';
import { 
  doc, 
  setDoc, 
  getDoc, 
  collection, 
  addDoc, 
  getDocs, 
  query, 
  where, 
  orderBy, 
  limit,
  startAfter,
  serverTimestamp,
  updateDoc,
  increment,
  runTransaction 
} from 'firebase/firestore';


// Get database instance - simplified since we have direct db import
const getDatabase = () => {
  return db;
};

// Generate unique username with max attempts to prevent infinite loops
export const generateUniqueUsername = async (baseUsername, maxAttempts = 100) => {
  let username = baseUsername.toLowerCase().replace(/[^a-z0-9]/g, '');

  // Ensure username is at least 3 characters
  if (username.length < 3) {
    username = username + '123';
  }

  // Check if username exists with attempt limit
  let counter = 0;
  let finalUsername = username;
  let attempts = 0;

  while (await checkUsernameExists(finalUsername) && attempts < maxAttempts) {
    counter++;
    finalUsername = `${username}${counter}`;
    attempts++;
  }

  if (attempts >= maxAttempts) {
    // Fallback: use timestamp-based unique identifier
    finalUsername = `${username}${Date.now().toString().slice(-6)}`;

  }

  return finalUsername;
};

// Check if username already exists - using usernames collection for atomicity
export const checkUsernameExists = async (username) => {
  const normalizedUsername = username.toLowerCase().trim();

  // Check if database is initialized
  if (!db) {
    return true; // Assume exists on error to be safe
  }

  try {
    // Check usernames collection for atomicity (maintained for data integrity)
    const usernameDocRef = doc(db, 'usernames', normalizedUsername);
    const usernameDoc = await getDoc(usernameDocRef);
    const exists = usernameDoc.exists();

    return exists;
  } catch (error) {
    return true; // Assume exists on error to be safe
  }
};

// Atomic username reservation using usernames collection (maintained for data integrity)
const reserveUsernameAtomically = async (baseUsername, userUid, userProfile) => {
  const maxAttempts = 100;
  let username = baseUsername.toLowerCase().replace(/[^a-z0-9]/g, '');

  // Ensure username is at least 3 characters
  if (username.length < 3) {
    username = username + '123';
  }

  return await runTransaction(db, async (transaction) => {
    let counter = 0;
    let finalUsername = username;
    let attempts = 0;

    // Try to find available username atomically using usernames collection
    while (attempts < maxAttempts) {
      const usernameDocRef = doc(db, 'usernames', finalUsername);
      const usernameDoc = await transaction.get(usernameDocRef);

      if (!usernameDoc.exists()) {
        // Username is available, reserve it atomically
        transaction.set(usernameDocRef, {
          userId: userUid,
          createdAt: serverTimestamp(),
        });

        const userDocRef = doc(db, 'users', userUid);
        transaction.set(userDocRef, {
          ...userProfile,
          username: finalUsername,
        });
        return { success: true, username: finalUsername, docRef: userDocRef };
      }

      // Username taken, try next variation
      counter++;
      finalUsername = `${username}${counter}`;
      attempts++;
    }

    // Fallback: use timestamp-based unique identifier
    finalUsername = `${username}${Date.now().toString().slice(-6)}`;

    const usernameDocRef = doc(db, 'usernames', finalUsername);
    transaction.set(usernameDocRef, {
      userId: userUid,
      createdAt: serverTimestamp(),
    });

    const userDocRef = doc(db, 'users', userUid);
    transaction.set(userDocRef, {
      ...userProfile,
      username: finalUsername,
    });
    return { success: true, username: finalUsername, docRef: userDocRef };
  });
};

// User Profile operations with atomic username reservation
export const createUserProfile = async (user) => {
  if (!user) return { success: false, error: 'No user provided' };

  const database = getDatabase();
  // Check if Firebase is configured
  if (!database) {

    return { success: false, error: 'Database not available' };
  }

  try {
      const userDocRef = doc(database, 'users', user.uid);
    const userDoc = await getDoc(userDocRef);

    if (!userDoc.exists()) {
      const { displayName, email, photoURL } = user;

      // Prepare user profile data
      const userProfile = {
        displayName,
        email,
        photoURL,
        bio: '',
        country: '',
        bannerImage: '',
        profileImage: photoURL || '',
        role: 'user', // Default role for new users (admin role assigned separately)
        supportersCount: 0,
        campaignsCount: 0,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
        profileCompleted: false, // Track if user has completed welcome popup
      };

      // Generate base username and reserve atomically
      const baseUsername = displayName || email?.split('@')[0] || 'user';
      const result = await reserveUsernameAtomically(baseUsername, user.uid, userProfile);

      if (result.success) {
        return { success: true, docRef: result.docRef, username: result.username };
      } else {
        return { success: false, error: 'Failed to reserve username' };
      }
    }

    return { success: true, docRef: userDocRef, existing: true };
  } catch (error) {
    if (process.env.NODE_ENV === 'development') {
      console.error('Error creating user profile:', error);
    }
    const errorResponse = await handleFirebaseError(error, 'firestore', { returnType: 'string' });
