          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {reports.map((report) => (
              <tr
                key={report.id}
                onClick={() => handleRowClick(report)}
                className={`cursor-pointer transition-colors ${
                  selectedReportId === report.id ? 'bg-emerald-50' : 'hover:bg-gray-50'
