import { NextResponse } from 'next/server';
import { adminFirestore } from '@/lib/firebaseAdmin';
import { sendNotificationToUser } from '@/utils/notifications/sendFCMNotification';
import { campaignUnderReviewTemplate } from '@/utils/notifications/notificationTemplates';

export async function POST(request) {
  try {
    const body = await request.json();

    const { campaignId, campaignSlug, reportedBy, reason, details } = body;

    // Validate required fields
    if (!campaignId || !reason) {
      return NextResponse.json(
        { success: false, error: 'Campaign ID and reason are required' },
        { status: 400 }
      );
    }

    // Validate reason is one of the allowed values
    const validReasons = ['inappropriate', 'spam', 'copyright', 'other'];
    if (!validReasons.includes(reason)) {
      return NextResponse.json(
        { success: false, error: 'Invalid report reason' },
        { status: 400 }
      );
    }

    // Get Firestore instance
    const db = adminFirestore();

    // Use Firestore transaction for atomic operations
    const reportRef = db.collection('reports').doc();
    const campaignRef = db.collection('campaigns').doc(campaignId);

    await db.runTransaction(async (transaction) => {
      // Get the campaign
      const campaignDoc = await transaction.get(campaignRef);

      if (!campaignDoc.exists) {
        throw new Error('Campaign not found');
      }

      const campaignData = campaignDoc.data();
      const currentReportsCount = campaignData.reportsCount || 0;
      const newReportsCount = currentReportsCount + 1;

      // Create the report
      const reportData = {
        campaignId,
        campaignSlug: campaignSlug || '',
        reportedBy: reportedBy || 'anonymous',
        reason,
        details: details || '',
        status: 'pending',
        createdAt: new Date(),
        reviewedAt: null,
        reviewedBy: null,
        action: null,
      };

      transaction.set(reportRef, reportData);

      // Update campaign
      const campaignUpdates = {
        reportsCount: newReportsCount,
        updatedAt: new Date(),
      };

      // Auto-flag for review if threshold reached (3+ reports)
      if (newReportsCount >= 3 && campaignData.moderationStatus === 'active') {
        campaignUpdates.moderationStatus = 'under-review-hidden';
        campaignUpdates.hiddenAt = new Date();
      }

      transaction.update(campaignRef, campaignUpdates);

      // Send notification if campaign is auto-hidden
      if (newReportsCount >= 3 && campaignData.moderationStatus === 'active' && campaignData.creatorId) {
        const notification = campaignUnderReviewTemplate(
          campaignData.title || 'Your campaign',
          `/campaign/${campaignData.slug || campaignId}`
        );

        await sendNotificationToUser(campaignData.creatorId, notification);
      }
    });

    return NextResponse.json(
      { success: true, reportId: reportRef.id },
      { status: 200 }
    );

  } catch (error) {
    console.error('Error creating report:', error);

    return NextResponse.json(
      { 
        success: false, 
        error: error.message || 'Failed to submit report. Please try again.' 
      },
      { status: 500 }
    );
  }
}

src/hooks/useSecureStorage.js.content:
// Custom hook for secure storage operations
import { useState, useCallback } from 'react'
import { uploadFile, uploadFileWithProgress, getSignedUrl, deleteFile, listFiles } from '../lib/supabase'
import { useAuth } from './useAuth'

export const useSecureStorage = () => {
  const { user } = useAuth()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [progress, setProgress] = useState(0)

  const handleError = useCallback((error) => {
    console.error('Storage operation error:', error)
    setError(error.message || 'An error occurred')
    return null
  }, [])

  const upload = useCallback(async (file, folder = 'uploads', withProgress = false) => {
    if (!user) {
      throw new Error('User must be authenticated to upload files')
    }

    setLoading(true)
    setError(null)
    setProgress(0)

    try {
      let result
      if (withProgress) {
        result = await uploadFileWithProgress(file, folder, setProgress)
      } else {
        result = await uploadFile(file, folder)
      }

      setProgress(100)
      return result
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const getUrl = useCallback(async (path, expiresIn = 3600) => {
    if (!user) {
      throw new Error('User must be authenticated to access files')
    }

    setLoading(true)
    setError(null)

    try {
      return await getSignedUrl(path, expiresIn)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const remove = useCallback(async (path) => {
    if (!user) {
      throw new Error('User must be authenticated to delete files')
    }

    setLoading(true)
    setError(null)

    try {
      return await deleteFile(path)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const list = useCallback(async (folder = 'uploads', limit = 100, offset = 0) => {
    if (!user) {
      throw new Error('User must be authenticated to list files')
    }

    setLoading(true)
    setError(null)

    try {
      return await listFiles(folder, limit, offset)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const clearError = useCallback(() => {
    setError(null)
  }, [])

  return {
    // State
    loading,
    error,
    progress,

    // Actions
    upload,
    getUrl,
    remove,
    list,
    clearError,

    // Utils
    isAuthenticated: !!user
  }
}

export default useSecureStorage

