"use client";

import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { getModerationStatusColor } from "@/utils/admin/adminHelpers";

export default function CampaignModerationCard({ campaign, onUpdate }) {
  const { user } = useAuth();
  const [showActions, setShowActions] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [deleteReason, setDeleteReason] = useState('');
  const [updateError, setUpdateError] = useState(null);

  const handleModerationChange = async (newStatus, reason = '') => {
    if (!user) return;

    setIsUpdating(true);
    setUpdateError(null);

    try {
      const token = await user.getIdToken();

      const response = await fetch(`/api/admin/campaigns/${campaign.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ 
          moderationStatus: newStatus,
          removeReason: reason 
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to update campaign');
      }

      if (onUpdate) {
        onUpdate(data.data);
      }

      setShowActions(false);
    } catch (error) {
      console.error('Error updating campaign:', error);
      setUpdateError(error.message);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleDelete = async () => {
    if (!user) return;

    setIsUpdating(true);
    setUpdateError(null);

    try {
      const token = await user.getIdToken();

      const response = await fetch(`/api/admin/campaigns/${campaign.id}/delete`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ 
          confirmed: true,
          deleteReason: deleteReason || 'Deleted by admin'
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to delete campaign');
      }

      if (onUpdate) {
        onUpdate(null, true);
      }

      setShowDeleteConfirm(false);
    } catch (error) {
      console.error('Error deleting campaign:', error);
      setUpdateError(error.message);
    } finally {
      setIsUpdating(false);
    }
  };

  return (
    <>
      <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
        <div className="relative">
          <img
            src={campaign.imageUrl}
            alt={campaign.title}
            className="w-full h-48 object-cover"
          />
          <div className="absolute top-2 right-2">
            <span className={`px-2 py-1 text-xs font-semibold rounded-full ${getModerationStatusColor(campaign.moderationStatus)}`}>
