src/app/firebase-messaging-sw/route.js.content:
import { NextResponse } from 'next/server';

export async function GET() {
  const requiredEnvVars = [
    'NEXT_PUBLIC_FIREBASE_API_KEY',
    'NEXT_PUBLIC_FIREBASE_PROJECT_ID',
    'NEXT_PUBLIC_FIREBASE_APP_ID',
    'NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID'
  ];

  const missingVars = requiredEnvVars.filter(varName => !process.env[varName]);

  if (missingVars.length > 0) {
    console.error('Missing Firebase environment variables:', missingVars);
    return new NextResponse(
      `// Firebase config incomplete. Missing: ${missingVars.join(', ')}`,
      {
        status: 500,
        headers: { 'Content-Type': 'application/javascript' }
      }
    );
  }

  const firebaseConfig = {
    apiKey: process.env.NEXT_PUBLIC_FIREBASE_API_KEY,
    authDomain: `${process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID}.firebaseapp.com`,
    projectId: process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID,
    storageBucket: `${process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID}.appspot.com`,
    messagingSenderId: process.env.NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.NEXT_PUBLIC_FIREBASE_APP_ID
  };

  const swContent = `
importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-app-compat.js');
importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-messaging-compat.js');

const firebaseConfig = ${JSON.stringify(firebaseConfig)};

firebase.initializeApp(firebaseConfig);

const messaging = firebase.messaging();

messaging.onBackgroundMessage((payload) => {
  console.log('Background notification received:', payload);

  const notificationTitle = payload.notification?.title || 'Twibbonize Notification';
  const notificationOptions = {
    body: payload.notification?.body || 'You have a new notification',
    icon: payload.notification?.icon || '/icon-192x192.png',
    badge: '/badge-72x72.png',
    data: {
      url: payload.data?.url || '/',
      ...payload.data
    },
    actions: [
      {
        action: 'open',
        title: 'View'
      },
      {
        action: 'close',
        title: 'Dismiss'
      }
    ]
  };

  self.registration.showNotification(notificationTitle, notificationOptions);
});

self.addEventListener('notificationclick', (event) => {
  event.notification.close();

  if (event.action === 'close') {
    return;
  }

  const urlToOpen = event.notification.data?.url || '/';

  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
      for (const client of clientList) {
        if (client.url === urlToOpen && 'focus' in client) {
          return client.focus();
        }
      }

      if (clients.openWindow) {
        return clients.openWindow(urlToOpen);
      }
    })
  );
});
  `.trim();

  return new NextResponse(swContent, {
    headers: {
      'Content-Type': 'application/javascript',
      'Service-Worker-Allowed': '/',
    },
  });
}

src/utils/notifications/sendFCMNotificat.content:
export async function sendFCMNotification({ userId, title, body, actionUrl, icon, data = {} }) {
  try {
    console.log('[FCM SEND] Preparing to send notification to userId:', userId);
    console.log('[FCM SEND] Notification details:', { title, body, actionUrl });

    const isServer = typeof window === 'undefined';
    const baseUrl = isServer 
      ? (process.env.NEXT_PUBLIC_SITE_URL || process.env.VERCEL_URL 
          ? `https://${process.env.VERCEL_URL}` 
          : 'http://localhost:3000')
      : '';

    const apiUrl = `${baseUrl}/api/notifications/send`;
    console.log('[FCM SEND] Using API URL:', apiUrl);

    const response = await fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId,
        title,
        body,
        actionUrl,
        icon,
        data
      }),
    });

    const result = await response.json();
    console.log('[FCM SEND] API response status:', response.status);
    console.log('[FCM SEND] API response data:', result);

    if (!response.ok) {
      console.error('[FCM SEND] API returned error:', result.error);
      throw new Error(result.error || 'Failed to send notification');
    }

    console.log('[FCM SEND] ✅ Notification sent successfully');
    return result;
  } catch (error) {
    console.error('[FCM SEND] ❌ Error sending FCM notification:', error);
    throw error;
  }
}

export async function sendBatchNotifications(notifications) {
  const promises = notifications.map(notification => 
    sendFCMNotification(notification).catch(err => {
      console.error('Failed to send notification:', err);
      return { success: false, error: err.message };
    })
  );

  return await Promise.allSettled(promises);
}

src/hooks/useFCM.js.content:
"use client";

import { useState, useEffect, useCallback } from 'react';
import { getToken, onMessage } from 'firebase/messaging';
import { messaging } from '@/lib/firebase-optimized';
import { useAuth } from '@/hooks/useAuth';

const VAPID_KEY = process.env.NEXT_PUBLIC_FIREBASE_VAPID_KEY;

export function useFCM() {
  const { user } = useAuth();
  const [notificationPermission, setNotificationPermission] = useState('default');
  const [fcmToken, setFcmToken] = useState(null);
  const [isSupported, setIsSupported] = useState(false);
  const [loading, setLoading] = useState(true);
  const [foregroundNotification, setForegroundNotification] = useState(null);

  useEffect(() => {
    if (typeof window !== 'undefined' && 'Notification' in window) {
      setNotificationPermission(Notification.permission);
      setIsSupported(true);
    }
    setLoading(false);
  }, []);

  useEffect(() => {
    const retrieveExistingToken = async () => {
      if (!isSupported || !user || notificationPermission !== 'granted' || fcmToken) {
        return;
      }

      const disabledByUser = localStorage.getItem('fcm_disabled_by_user') === 'true';
      if (disabledByUser) {
        return;
      }

      try {
        const messagingInstance = messaging;
        if (!messagingInstance || !VAPID_KEY) {
          return;
        }

        let registration;
        if ('serviceWorker' in navigator) {
          registration = await navigator.serviceWorker.getRegistration('/');
          if (!registration) {
            registration = await navigator.serviceWorker.register(
              '/firebase-messaging-sw',
              { scope: '/' }
            );
          }
          await navigator.serviceWorker.ready;
        }

        const token = await getToken(messagingInstance, {
          vapidKey: VAPID_KEY,
          serviceWorkerRegistration: registration
        });

        if (token) {
          setFcmToken(token);
          console.log('Existing FCM token retrieved:', token);
        }
      } catch (error) {
        console.error('Error retrieving existing FCM token:', error);
      }
    };

    retrieveExistingToken();
  }, [isSupported, user, notificationPermission, fcmToken]);

  const requestPermission = useCallback(async () => {
    if (!isSupported || !user) {
      console.log('FCM not supported or user not authenticated');
      return null;
    }

    try {
      const permission = await Notification.requestPermission();
      setNotificationPermission(permission);

      if (permission === 'granted') {
        localStorage.removeItem('fcm_disabled_by_user');

        const messagingInstance = messaging;

        if (!messagingInstance) {
          console.error('Firebase Messaging not initialized');
          return null;
        }

        if (!VAPID_KEY) {
          console.error('VAPID key not configured');
          return null;
        }

        let registration;
        if ('serviceWorker' in navigator) {
          registration = await navigator.serviceWorker.register(
            '/firebase-messaging-sw',
            { scope: '/' }
          );
          await navigator.serviceWorker.ready;
          console.log('Service Worker registered:', registration);
        }

        const token = await getToken(messagingInstance, { 
          vapidKey: VAPID_KEY,
          serviceWorkerRegistration: registration
        });
        setFcmToken(token);

        const browser = navigator.userAgent.includes('Chrome') ? 'Chrome' :
                       navigator.userAgent.includes('Firefox') ? 'Firefox' :
                       navigator.userAgent.includes('Safari') ? 'Safari' : 'Unknown';

        await fetch('/api/notifications/register-token', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: user.uid,
            token,
            device: 'web',
            browser
          }),
        });

        return token;
      }

      return null;
    } catch (error) {
      console.error('Error requesting notification permission:', error);
      return null;
    }
  }, [user, isSupported]);

  const removeToken = useCallback(async () => {
    if (!user || !fcmToken) return;

    try {
      localStorage.setItem('fcm_disabled_by_user', 'true');

      await fetch('/api/notifications/remove-token', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.uid,
          token: fcmToken
        }),
      });

      setFcmToken(null);
    } catch (error) {
      console.error('Error removing FCM token:', error);
    }
  }, [user, fcmToken]);

  useEffect(() => {
    if (!messaging || !fcmToken) return;

    const unsubscribe = onMessage(messaging, (payload) => {
      console.log('Foreground notification received:', payload);

      if (payload.notification) {
        setForegroundNotification(payload);
      }
    });

    return () => unsubscribe();
  }, [fcmToken]);

  const clearNotification = useCallback(() => {
    setForegroundNotification(null);
  }, []);

  return {
    notificationPermission,
    fcmToken,
    isSupported,
    loading,
    requestPermission,
    removeToken,
    foregroundNotification,
    clearNotification,
  };
}

src/app/api/notifications/send/route.js.content:
import { NextResponse } from 'next/server';
import adminApp, { adminFirestore } from '@/lib/firebaseAdmin';
import { getMessaging } from 'firebase-admin/messaging';

export async function POST(request) {
  try {
    const body = await request.json();
    const { userId, title, body: messageBody, actionUrl, icon, data } = body;

    console.log('[SEND API] Received notification request for userId:', userId);
    console.log('[SEND API] Notification:', { title, messageBody, actionUrl });

    if (!userId || !title || !messageBody) {
      console.error('[SEND API] Missing required fields');
      return NextResponse.json(
        { error: 'Missing required fields: userId, title, and body' },
        { status: 400 }
      );
    }

    const db = adminFirestore();
    const messaging = getMessaging(adminApp);

    console.log('[SEND API] Fetching FCM tokens for user...');
    const tokensSnapshot = await db
      .collection('users')
      .doc(userId)
      .collection('tokens')
      .get();

    if (tokensSnapshot.empty) {
      console.log('[SEND API] No FCM tokens found for user:', userId);
      return NextResponse.json({
        success: false,
        message: 'No FCM tokens found for this user'
      });
    }

    const tokens = tokensSnapshot.docs.map(doc => doc.data().token);
    console.log('[SEND API] Found', tokens.length, 'FCM token(s) for user');

    const message = {
      notification: {
        title,
        body: messageBody,
        icon: icon || '/icon-192x192.png',
      },
      data: {
        url: actionUrl || '/',
        ...data
      },
      tokens
    };

    console.log('[SEND API] Sending notification via Firebase Admin SDK...');
    const response = await messaging.sendEachForMulticast(message);
    console.log('[SEND API] Firebase response - Success:', response.successCount, 'Failed:', response.failureCount);

    const failedTokens = [];
    response.responses.forEach((resp, idx) => {
      if (!resp.success) {
        failedTokens.push(tokens[idx]);
        console.error('[SEND API] Failed to send to token:', tokens[idx], 'Error:', resp.error);
      }
    });

    if (failedTokens.length > 0) {
      console.log('[SEND API] Cleaning up', failedTokens.length, 'failed token(s)...');
      const batch = db.batch();
      failedTokens.forEach(token => {
        const tokenRef = db.collection('users').doc(userId).collection('tokens').doc(token);
        batch.delete(tokenRef);
      });
      await batch.commit();
      console.log('[SEND API] Failed tokens removed from database');
    }

    console.log('[SEND API] ✅ Notification sending complete');
    return NextResponse.json({
      success: true,
      successCount: response.successCount,
      failureCount: response.failureCount,
      message: `Sent ${response.successCount} notifications, ${response.failureCount} failed`
    });

  } catch (error) {
    console.error('Error sending FCM notification:', error);
    return NextResponse.json(
      { error: 'Failed to send notification', details: error.message },
      { status: 500 }
    );
  }
}

