export default function AuthGate({ children }) {
  if (typeof window !== 'undefined') {
    console.log('ðŸš§ AuthGate rendering on route:', window.location.pathname);
  }

  const authContext = useOptionalAuth();

  // If no auth context, just render children
  if (!authContext) {
    if (typeof window !== 'undefined') {
      console.log('â„¹ï¸ AuthGate: No auth context, rendering children directly');
    }
