// Validation utilities for authentication forms

export const validateEmail = (email) => {
  if (!email) return 'Email is required';
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(email)) return 'Please enter a valid email address';
  return null;
};

export const validatePassword = (password, isSignUp = false) => {
  if (!password) return 'Password is required';
  if (isSignUp && password.length < 8) return 'Password must be at least 8 characters';
  return null;
};

export const validateFullName = (fullName) => {
  if (!fullName) return 'Full name is required';
  if (fullName.trim().length < 2) return 'Full name must be at least 2 characters long';
  if (fullName.length > 50) return 'Full name must be less than 50 characters';
  return null;
};

export const normalizeEmail = (email) => {
  return email ? email.toLowerCase().trim() : '';
};

