@import "tailwindcss";

:root {
  --background: #ffffff;
  --foreground: #171717;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

/* Global Button Standardization - Consistent styling for all buttons */

/* Base button class - Applied to ALL buttons for consistency */
.btn-base {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 0.5rem; /* rounded-lg - 8px */
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  transform: scale(1);
  outline: none;
}

.btn-base:hover {
  transform: scale(1.03);
}

.btn-base:focus {
  outline: 2px solid #3b82f6; /* blue-500 for universal focus visibility */
  outline-offset: 2px;
}

/* Primary button styling */
.btn-primary {
  background: linear-gradient(to right, #059669, #047857); /* emerald-600 to emerald-700 */
  color: white;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1);
}

.btn-primary:hover {
  background: linear-gradient(to right, #047857, #065f46); /* emerald-700 to emerald-800 */
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1);
}

.btn-primary:focus {
  outline: 2px solid #10b981; /* emerald-500 focus ring for primary buttons */
  outline-offset: 2px;
}

/* Secondary button styling */
.btn-secondary {
  background: white;
  border: 1px solid #d1d5db; /* gray-300 */
  color: #374151; /* gray-700 */
}

.btn-secondary:hover {
  background: #f9fafb; /* gray-50 */
  border-color: #9ca3af; /* gray-400 */
}

.btn-secondary:focus {
  outline: 2px solid #6b7280; /* gray-500 focus ring for secondary buttons */
  outline-offset: 2px;
}

/* Danger button styling */
.btn-danger {
  background: #dc2626; /* red-600 */
  color: white;
}

.btn-danger:hover {
  background: #b91c1c; /* red-700 */
}

.btn-danger:focus {
  outline: 2px solid #ef4444; /* red-500 focus ring for danger buttons */
  outline-offset: 2px;
}

/* Warning button styling */
.btn-warning {
  background: #eab308; /* yellow-500 */
  color: white;
}

.btn-warning:hover {
  background: #ca8a04; /* yellow-600 */
}

.btn-warning:focus {
  outline: 2px solid #facc15; /* yellow-400 focus ring for warning buttons */
  outline-offset: 2px;
}

/* Google/Social button styling */
.btn-google {
  background: white;
  border: 1px solid #d1d5db; /* gray-300 */
  color: #374151; /* gray-700 */
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1);
}

.btn-google:hover {
  background: #fef3c7; /* yellow-50 */
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1);
}

/* Link button styling (text buttons) */
.btn-link {
  background: transparent;
  color: #059669; /* emerald-600 */
  text-decoration: none;
  border: none;
  padding: 0;
}

.btn-link:hover {
  color: #047857; /* emerald-700 */
  text-decoration: underline;
  transform: scale(1.02); /* Subtle zoom for text buttons */
}

/* Disabled state for all buttons */
.btn-base:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: scale(1);
}

.btn-base:disabled:hover {
  transform: scale(1);
}

/* Legacy hover-zoom class - kept for backward compatibility but enhanced */
.hover-zoom {
  transform: scale(1);
  transition: transform 0.2s ease-in-out;
  cursor: pointer;
}

.hover-zoom:hover {
  transform: scale(1.03);
}

@media (prefers-color-scheme: dark) {
  :root {
    --background: #0a0a0a;
    --foreground: #ededed;
  }
}

body {
  background: var(--background);
  color: var(--foreground);
  font-family: Arial, Helvetica, sans-serif;
}

src/app/layout.js.content:
import { Geist, Geist_Mono } from "next/font/google";
import "./globals.css";
import ClientAuthProvider from "../components/ClientAuthProvider";
import ErrorBoundary from "../components/ErrorBoundary";
import TimeoutWrapper from "../components/TimeoutWrapper";
import AuthenticatedLayout from "../components/AuthenticatedLayout";
import AuthGate from "../components/AuthGate";
import { CampaignSessionProvider } from "../contexts/CampaignSessionContext";

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
});

export const metadata = {
  title: "Frame Your Voice - Twibbonize App",
  description: "Create and share frames that amplify your message, celebrate your cause, and inspire others to join in.",
};

export default function RootLayout({ children }) {
  return (
    <html lang="en" suppressHydrationWarning={true}>
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased`}
        suppressHydrationWarning={true}
      >
        <ErrorBoundary>
          <TimeoutWrapper timeout={15000}>
            <ClientAuthProvider>
              <CampaignSessionProvider>
                <AuthenticatedLayout>
                  <AuthGate>
                    {children}
                  </AuthGate>
                </AuthenticatedLayout>
              </CampaignSessionProvider>
            </ClientAuthProvider>
          </TimeoutWrapper>
        </ErrorBoundary>
      </body>
    </html>
  );
}

