src/app/api/storage/upload-url/route.js.content:
import { NextResponse } from 'next/server'
import { verifyIdToken } from '../../../../lib/firebaseAdmin'
import { supabaseAdmin } from '../../../../lib/supabase-admin'
import { headers } from 'next/headers'

// Force Node.js runtime for server-side operations
export const runtime = 'nodejs'

export async function POST(request) {
  try {
    // Get the authorization header
    const headersList = headers()
    const authorization = headersList.get('authorization')

    if (!authorization || !authorization.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'No authorization token provided' }, { status: 401 })
    }

    const token = authorization.replace('Bearer ', '')

    // Verify Firebase ID token
    let decodedToken
    try {
      decodedToken = await verifyIdToken(token)
    } catch (error) {
      return NextResponse.json({ error: 'Invalid token' }, { status: 401 })
    }

    const { fileName, fileType, fileSize, folder = 'uploads' } = await request.json()

    // Validate folder (whitelist allowed folders)
    const allowedFolders = ['uploads', 'profile-images', 'documents', 'temp', 'campaigns']
    if (!allowedFolders.includes(folder)) {
      return NextResponse.json({ error: 'Invalid folder' }, { status: 400 })
    }

    // Validate required fields
    if (!fileName) {
      return NextResponse.json({ error: 'fileName is required' }, { status: 400 })
    }

    if (!Number.isFinite(fileSize) || fileSize <= 0) {
      return NextResponse.json({ 
        error: 'fileSize is required and must be a positive number' 
      }, { status: 400 })
    }

    if (!fileType || typeof fileType !== 'string' || fileType.trim() === '') {
      return NextResponse.json({ 
        error: 'fileType is required and must be a non-empty string' 
      }, { status: 400 })
    }

    // Validate file size (5MB max for campaigns, 10MB max for others)
    const maxFileSize = folder === 'campaigns' ? 5 * 1024 * 1024 : 10 * 1024 * 1024
    if (fileSize > maxFileSize) {
      const maxSizeMB = folder === 'campaigns' ? 5 : 10
      return NextResponse.json({ 
        error: `File size exceeds ${maxSizeMB}MB limit` 
      }, { status: 400 })
    }

    // Validate file type for campaigns
    if (folder === 'campaigns') {
      const allowedTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/webp']
      if (!allowedTypes.includes(fileType.toLowerCase())) {
        return NextResponse.json({ 
          error: 'Invalid file type for campaigns. Only PNG, JPG, and WEBP are allowed.' 
        }, { status: 400 })
      }
    }

    // Create user-specific path
    const timestamp = Date.now()
    const sanitizedFileName = fileName.replace(/[^a-zA-Z0-9.-]/g, '_')
    const filePath = `${folder}/${decodedToken.uid}/${timestamp}-${sanitizedFileName}`

    // Generate signed upload URL (valid for 1 hour)
    const { data, error } = await supabaseAdmin.storage
      .from('uploads')
      .createSignedUploadUrl(filePath)

    if (error) {
      console.error('Supabase error:', error)
      return NextResponse.json({ error: 'Failed to create upload URL' }, { status: 500 })
    }

    return NextResponse.json({
      uploadUrl: data.signedUrl,
      path: filePath,
      token: data.token
    })

  } catch (error) {
    console.error('Upload URL generation error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

src/hooks/useSecureStorage.js.content:
// Custom hook for secure storage operations
import { useState, useCallback } from 'react'
import { uploadFile, uploadFileWithProgress, getSignedUrl, deleteFile, listFiles } from '../lib/supabase'
import { useAuth } from './useAuth'

export const useSecureStorage = () => {
  const { user } = useAuth()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [progress, setProgress] = useState(0)

  const handleError = useCallback((error) => {
    console.error('Storage operation error:', error)
    setError(error.message || 'An error occurred')
    return null
  }, [])

  const upload = useCallback(async (file, folder = 'uploads', withProgress = false) => {
    if (!user) {
      throw new Error('User must be authenticated to upload files')
    }

    setLoading(true)
    setError(null)
    setProgress(0)

    try {
      let result
      if (withProgress) {
        result = await uploadFileWithProgress(file, folder, setProgress)
      } else {
        result = await uploadFile(file, folder)
      }

      setProgress(100)
      return result
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const getUrl = useCallback(async (path, expiresIn = 3600) => {
    if (!user) {
      throw new Error('User must be authenticated to access files')
    }

    setLoading(true)
    setError(null)

    try {
      return await getSignedUrl(path, expiresIn)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const remove = useCallback(async (path) => {
    if (!user) {
      throw new Error('User must be authenticated to delete files')
    }

    setLoading(true)
    setError(null)

    try {
      return await deleteFile(path)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const list = useCallback(async (folder = 'uploads', limit = 100, offset = 0) => {
    if (!user) {
      throw new Error('User must be authenticated to list files')
    }

    setLoading(true)
    setError(null)

    try {
      return await listFiles(folder, limit, offset)
    } catch (error) {
      return handleError(error)
    } finally {
      setLoading(false)
    }
  }, [user, handleError])

  const clearError = useCallback(() => {
    setError(null)
  }, [])

  return {
    // State
    loading,
    error,
    progress,

    // Actions
    upload,
    getUrl,
    remove,
    list,
    clearError,

    // Utils
    isAuthenticated: !!user
  }
}

export default useSecureStorage

