'use client';

import { useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '../../hooks/useAuth';
import EmailVerification from '../../components/EmailVerification';
import PageLoader from '../../components/PageLoader';

export default function VerifyEmailPage() {
  const router = useRouter();
  const { user, loading, pendingSignupUserId, logoutInProgress } = useAuth();
  const safetyTimeoutRef = useRef(null);

  useEffect(() => {
    // Redirect to homepage if user is verified
    if (user && !loading && user.emailVerified) {
      router.replace('/');
      return;
    }

    // Clear any existing safety timeout
    if (safetyTimeoutRef.current) {
      clearTimeout(safetyTimeoutRef.current);
      safetyTimeoutRef.current = null;
    }

    // Only redirect to signin if auth is done loading, there's no user, no pending signup,
    // AND logout is not in progress (prevents redirect override on intentional logout)
    if (!loading && !user && !pendingSignupUserId && !logoutInProgress) {
      // Add safety fallback with debounce to avoid stuck loader state
      safetyTimeoutRef.current = setTimeout(() => {
        router.replace('/signin');
      }, 500);
    }
  }, [user, loading, pendingSignupUserId, logoutInProgress, router]);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (safetyTimeoutRef.current) {
        clearTimeout(safetyTimeoutRef.current);
      }
    };
  }, []);

  // Show loader while auth is loading
  if (loading) {
    return <PageLoader message="Loading..." />;
  }

  // Show loader while pending signup (waiting for auth state to update)
  if (pendingSignupUserId) {
    return <PageLoader message="Setting up your account..." />;
  }

  // Show loader while redirecting if no user
  if (!user) {
    return <PageLoader message="Redirecting..." />;
  }

  // Show email verification if user exists but is not verified
  if (user && !user.emailVerified) {
    return <EmailVerification />;
  }

  // Fallback - shouldn't reach here but just in case
  return <PageLoader message="Redirecting..." />;
}

src/components/ConfirmationModal.js.content:
"use client";

import { useEffect } from 'react';

export default function ConfirmationModal({ 
  isOpen, 
  onClose, 
  onConfirm, 
  title = "Confirm Action", 
  message = "Are you sure you want to proceed?",
  confirmText = "Confirm",
  cancelText = "Cancel",
  type = "danger" // "danger" or "warning"
}) {
  // Close modal on Escape key
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      // Prevent body scroll when modal is open
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleConfirm = () => {
    onConfirm();
    onClose();
  };

  return (
    <div 
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
      onClick={handleBackdropClick}
    >
      <div className="bg-white rounded-xl shadow-2xl max-w-md w-full mx-4 transform transition-all">
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center gap-3">
            {type === "danger" ? (
              <div className="w-10 h-10 bg-red-100 rounded-full flex items-center justify-center flex-shrink-0">
                <svg className="w-5 h-5 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
            ) : (
              <div className="w-10 h-10 bg-yellow-100 rounded-full flex items-center justify-center flex-shrink-0">
                <svg className="w-5 h-5 text-yellow-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
            )}
            <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
          </div>
        </div>

        {/* Body */}
        <div className="px-6 py-4">
          <p className="text-gray-700 text-sm leading-relaxed">{message}</p>
        </div>

        {/* Footer */}
        <div className="px-6 py-4 bg-gray-50 rounded-b-xl flex gap-3 justify-end">
          <button
            onClick={onClose}
            className="btn-base btn-secondary px-4 py-2 text-sm"
          >
            {cancelText}
          </button>
          <button
            onClick={handleConfirm}
            className={`btn-base px-4 py-2 text-sm ${
              type === "danger" 
                ? "btn-danger" 
                : "btn-warning"
            }`}
          >
            {confirmText}
          </button>
        </div>
      </div>
    </div>
  );
}

src/components/ConditionalLayout.js.content:
"use client";

import { usePathname } from 'next/navigation';
import { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import MobileMenu from './MobileMenu';
import { useLayoutVisibility } from './LayoutVisibilityContext';

// Pages where header/footer should NOT be shown
const EXCLUDED_PAGES = [
  '/signin',
  '/signup',
  '/forgot-password',
  '/onboarding',
  '/verify-email'
];

export default function ConditionalLayout({ children }) {
  const pathname = usePathname();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { hideChrome } = useLayoutVisibility();

  // Check if current page should exclude header/footer
  const shouldExcludeLayout = hideChrome || EXCLUDED_PAGES.includes(pathname);

  // If layout should be excluded, just return children
  if (shouldExcludeLayout) {
    return children;
  }

  // Show header/footer for all other pages
  return (
    <div className="min-h-screen flex flex-col relative">
      {/* Main Content with blur effect */}
      <div className={`min-h-screen flex flex-col transition-all duration-300 ${
        isMenuOpen ? 'blur-sm' : ''
      }`}>
        <Header isMenuOpen={isMenuOpen} setIsMenuOpen={setIsMenuOpen} />
        <main className="flex-1">
          {children}
        </main>
        <Footer />
      </div>

      {/* Mobile Menu Component */}
      <MobileMenu 
        isMenuOpen={isMenuOpen}
        setIsMenuOpen={setIsMenuOpen}
      />
    </div>
  );
}

