src/utils/campaignStorage.js.content:
/**
 * Campaign Storage Utilities
 * 
 * Helper functions for working with campaign image storage paths.
 * Ensures consistent path structure: campaigns/{userId}/{campaignId}.png
 */

/**
 * Build the storage path for a campaign image
 * 
 * @param {string} userId - User ID (creator of the campaign)
 * @param {string} campaignId - Campaign ID
 * @returns {string} Storage path: campaigns/{userId}/{campaignId}.png
 * 
 * @example
 * const path = buildCampaignImagePath('user123', 'campaign456');
 * // Returns: "campaigns/user123/campaign456.png"
 */
export function buildCampaignImagePath(userId, campaignId) {
  if (!userId || !campaignId) {
    throw new Error('userId and campaignId are required');
  }

  return `campaigns/${userId}/${campaignId}.png`;
}

/**
 * Build the public URL for a campaign image
 * 
 * @param {string} userId - User ID (creator of the campaign)
 * @param {string} campaignId - Campaign ID
 * @returns {string} Public URL to the campaign image
 * 
 * @example
 * const url = buildCampaignImageUrl('user123', 'campaign456');
 * // Returns: "https://{project}.supabase.co/storage/v1/object/public/uploads/campaigns/user123/campaign456.png"
 */
export function buildCampaignImageUrl(userId, campaignId) {
  const path = buildCampaignImagePath(userId, campaignId);
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;

  if (!supabaseUrl) {
    throw new Error('NEXT_PUBLIC_SUPABASE_URL is not configured');
  }

  return `${supabaseUrl}/storage/v1/object/public/uploads/${path}`;
}

/**
 * Extract userId and campaignId from a campaign image path
 * 
 * @param {string} path - Storage path
 * @returns {{userId: string, campaignId: string} | null} Extracted IDs or null if invalid
 * 
 * @example
 * const ids = parseCampaignImagePath('campaigns/user123/campaign456.png');
 * // Returns: { userId: 'user123', campaignId: 'campaign456' }
 */
export function parseCampaignImagePath(path) {
  if (!path) return null;

  // Match pattern: campaigns/{userId}/{campaignId}.png
  const match = path.match(/^campaigns\/([^/]+)\/([^/]+)\.png$/);

  if (!match) return null;

  return {
    userId: match[1],
    campaignId: match[2]
  };
}

/**
 * Validate if a path matches the campaign storage structure
 * 
 * @param {string} path - Storage path to validate
 * @returns {boolean} True if path matches campaigns/{userId}/{campaignId}.png
 * 
 * @example
 * isValidCampaignPath('campaigns/user123/campaign456.png'); // true
 * isValidCampaignPath('uploads/user123/file.png'); // false
 */
export function isValidCampaignPath(path) {
  return parseCampaignImagePath(path) !== null;
}

/**
 * Get campaign upload URL from API
 * Client-side helper for uploading campaign images
 * 
 * @param {string} campaignId - Campaign ID
 * @param {number} fileSize - File size in bytes
 * @param {string} fileType - MIME type of the file
 * @param {string} authToken - Firebase auth token
 * @returns {Promise<{uploadUrl: string, path: string, token: string}>}
 * 
 * @example
 * const { uploadUrl, path } = await getCampaignUploadUrl(campaignId, file.size, file.type, token);
 * await fetch(uploadUrl, {
 *   method: 'PUT',
 *   body: imageFile,
 *   headers: { 'Content-Type': 'image/png' }
 * });
 */
export async function getCampaignUploadUrl(campaignId, fileSize, fileType, authToken) {
  const response = await fetch('/api/storage/campaign-upload-url', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${authToken}`
    },
    body: JSON.stringify({ campaignId, fileSize, fileType })
  });

  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.error || 'Failed to get upload URL');
  }

  return response.json();
}

/**
 * Delete a campaign image from storage
 * Client-side helper for deleting campaign images
 * 
 * @param {string} path - Storage path to delete
 * @param {string} authToken - Firebase auth token
 * @returns {Promise<{success: boolean}>}
 * 
 * @example
 * const path = buildCampaignImagePath(userId, campaignId);
 * await deleteCampaignImage(path, token);
 */
export async function deleteCampaignImage(path, authToken) {
  const response = await fetch('/api/storage/delete', {
    method: 'DELETE',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${authToken}`
    },
    body: JSON.stringify({ path })
  });

  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.error || 'Failed to delete image');
  }

  return response.json();
}

src/utils/slugGenerator.js.content:
/**
 * Generate URL-friendly slug from campaign title
 * 
 * Algorithm (from CAMPAIGN_SYSTEM.md):
 * 1. Convert title to lowercase
 * 2. Replace spaces with hyphens
 * 3. Remove special characters
 * 4. Append random 4-character suffix (alphanumeric, base36)
 * 5. No uniqueness check needed (collision probability extremely low)
 * 
 * @param {string} title - Campaign title
 * @returns {string} URL-friendly slug
 * 
 * @example
 * generateSlug("Save Earth 2025") // => "save-earth-2025-k8m3"
 * generateSlug("My Frame!") // => "my-frame-a7b2"
 */
export function generateSlug(title) {
  if (!title || typeof title !== 'string') {
    throw new Error('Title is required and must be a string');
  }

  // Step 1-3: Convert to lowercase, remove special chars, replace spaces
  const baseSlug = title
    .toLowerCase()
    .trim()
    .replace(/[^a-z0-9\s-]/g, '')  // Remove special chars (keep letters, numbers, spaces, hyphens)
    .replace(/\s+/g, '-')           // Spaces to hyphens
    .replace(/-+/g, '-')            // Multiple hyphens to single
    .substring(0, 50);              // Max 50 chars for base slug

  // Handle edge case: empty slug after sanitization
  if (!baseSlug) {
    return `campaign-${generateRandomSuffix()}`;
  }

  // Step 4: Generate 4-char random suffix (0-9, a-z)
  const suffix = generateRandomSuffix();

  return `${baseSlug}-${suffix}`;
}

/**
 * Generate random 4-character alphanumeric suffix
 * Uses base36 (0-9, a-z) for URL-safe characters
 * 
 * @returns {string} 4-character random suffix
 */
function generateRandomSuffix() {
  return Math.random().toString(36).substring(2, 6).padEnd(4, '0');
}

/**
 * Validate if a string is a valid slug format
 * 
 * @param {string} slug - Slug to validate
 * @returns {boolean} True if valid slug format
 */
export function isValidSlug(slug) {
  if (!slug || typeof slug !== 'string') {
    return false;
  }

  // Valid slug: lowercase letters, numbers, hyphens only
  // Must end with 4-character suffix pattern
  const slugPattern = /^[a-z0-9]+(-[a-z0-9]+)*-[a-z0-9]{4}$/;
  return slugPattern.test(slug);
}

src/utils/transparencyDetector.js.content:
/**
 * Transparency Detection Utility for Frame Validation
 * 
 * Uses Canvas API to analyze images for transparency.
 * Frames must have at least 5% transparent pixels to be valid.
 * 
 * Based on algorithm from CAMPAIGN_SYSTEM.md
 */

/**
 * Check if an image file has sufficient transparency for a frame
 * 
 * @param {File} imageFile - Image file to analyze
 * @param {number} minTransparencyPercent - Minimum transparency required (default: 5%)
 * @returns {Promise<{hasTransparency: boolean, transparencyPercent: number, error?: string}>}
 * 
 * @example
 * const result = await checkTransparency(file);
 * if (result.hasTransparency) {
 *   console.log(`Valid frame with ${result.transparencyPercent.toFixed(2)}% transparency`);
 * } else {
 *   console.error(result.error);
 * }
 */
export async function checkTransparency(imageFile, minTransparencyPercent = 5) {
  if (!imageFile) {
    return {
      hasTransparency: false,
      transparencyPercent: 0,
      error: 'No image file provided'
    };
  }

  // Check if file is an image
  if (!imageFile.type.startsWith('image/')) {
    return {
      hasTransparency: false,
      transparencyPercent: 0,
      error: 'File is not an image'
    };
  }

  // PNG is required for transparency (check file type)
  if (!imageFile.type.includes('png')) {
    return {
      hasTransparency: false,
      transparencyPercent: 0,
      error: 'Frame must be a PNG image with transparency. Please use PNG format.'
    };
  }

  try {
    const result = await analyzeImageTransparency(imageFile);

    if (result.error) {
      return {
        hasTransparency: false,
        transparencyPercent: 0,
        error: result.error
      };
    }

    const hasTransparency = result.transparencyPercent >= minTransparencyPercent;

    if (!hasTransparency) {
      return {
        hasTransparency: false,
        transparencyPercent: result.transparencyPercent,
        error: `Frame must have at least ${minTransparencyPercent}% transparent area for photos. Current: ${result.transparencyPercent.toFixed(2)}%`
      };
    }

    return {
      hasTransparency: true,
      transparencyPercent: result.transparencyPercent
    };
  } catch (error) {
    return {
      hasTransparency: false,
      transparencyPercent: 0,
      error: `Failed to analyze image: ${error.message}`
    };
  }
}

/**
 * Analyze image transparency using Canvas API
 * 
 * @param {File} imageFile - Image file to analyze
 * @returns {Promise<{transparencyPercent: number, error?: string}>}
 */
async function analyzeImageTransparency(imageFile) {
  return new Promise((resolve, reject) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      try {
        // Set canvas size to image size
        canvas.width = img.width;
        canvas.height = img.height;

        // Draw image to canvas
        ctx.drawImage(img, 0, 0);

        // Get image data (RGBA pixel array)
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
        const pixels = imageData.data;

        let transparentPixels = 0;
        const totalPixels = canvas.width * canvas.height;

        // Check alpha channel of each pixel
        // pixels array format: [r, g, b, a, r, g, b, a, ...]
        // Alpha channel is every 4th value (index 3, 7, 11, ...)
        for (let i = 3; i < pixels.length; i += 4) {
          // Alpha value < 255 means some transparency
          if (pixels[i] < 255) {
            transparentPixels++;
          }
        }

        const transparencyPercent = (transparentPixels / totalPixels) * 100;

        // Clean up
        URL.revokeObjectURL(img.src);

        resolve({
          transparencyPercent,
          totalPixels,
          transparentPixels
        });
      } catch (error) {
        reject(new Error(`Canvas analysis failed: ${error.message}`));
      }
    };

    img.onerror = () => {
      URL.revokeObjectURL(img.src);
      reject(new Error('Failed to load image'));
    };

    // Create object URL for the image file
    img.src = URL.createObjectURL(imageFile);
  });
}

/**
 * Quick check if file type supports transparency
 * 
 * @param {File} imageFile - Image file to check
 * @returns {boolean} True if file type supports transparency
 */
export function supportsTransparency(imageFile) {
  if (!imageFile || !imageFile.type) {
    return false;
  }

  // PNG supports transparency, JPG/JPEG does not
  return imageFile.type.includes('png');
}

/**
 * Get transparency info for display purposes
 * 
 * @param {File} imageFile - Image file to analyze
 * @returns {Promise<{valid: boolean, message: string, percent?: number}>}
 */
export async function getTransparencyInfo(imageFile) {
  const result = await checkTransparency(imageFile);

  if (result.hasTransparency) {
    return {
      valid: true,
      message: `Valid frame with ${result.transparencyPercent.toFixed(1)}% transparency`,
      percent: result.transparencyPercent
    };
  } else {
    return {
      valid: false,
      message: result.error || 'Invalid transparency',
      percent: result.transparencyPercent
    };
  }
}

