/**
 * Canvas-based image composition utility for Twibbonize
 * Handles overlaying user photos with frames and backgrounds
 */

/**
 * Load an image from a File object or URL
 * @param {File|string} source - Image file or URL
 * @returns {Promise<HTMLImageElement>} Loaded image element
 */
export const loadImage = (source) => {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.crossOrigin = 'anonymous'; // Enable CORS for external images

    img.onload = () => {
      resolve(img);
    };

    img.onerror = (error) => {
      reject(new Error('Failed to load image'));
    };

    // Handle File object vs URL string
    if (source instanceof File || source instanceof Blob) {
      const objectUrl = URL.createObjectURL(source);
      img.src = objectUrl;
      // Store URL for cleanup
      img.dataset.objectUrl = objectUrl;
    } else {
      img.src = source;
    }
  });
};

/**
 * Compose user photo with campaign image
 * @param {File} userPhotoFile - User's uploaded photo
 * @param {string} campaignImageUrl - URL of campaign image (frame or background)
 * @param {object} adjustments - Photo adjustments {scale: 1.0, x: 0, y: 0, rotation: 0}
 * @param {string} campaignType - 'frame' or 'background'
 * @returns {Promise<{canvas: HTMLCanvasElement, blob: Blob}>} Composed canvas and blob for download
 */
export const composeImages = async (userPhotoFile, campaignImageUrl, adjustments = {}, campaignType = 'frame') => {
  // Default adjustments
  const { scale = 1.0, x = 0, y = 0, rotation = 0 } = adjustments;

  try {
    // Load both images
    const [userPhoto, campaignImage] = await Promise.all([
      loadImage(userPhotoFile),
      loadImage(campaignImageUrl)
    ]);

    // Create canvas matching campaign image dimensions
    const canvas = document.createElement('canvas');
    canvas.width = campaignImage.width;
    canvas.height = campaignImage.height;

    const ctx = canvas.getContext('2d', { alpha: true });

    if (!ctx) {
      throw new Error('Failed to get canvas context');
    }

    // Compose based on campaign type
    if (campaignType === 'frame') {
      // Frame: User photo UNDER frame (frame overlays on top)
      drawUserPhotoWithAdjustments(ctx, userPhoto, canvas.width, canvas.height, scale, x, y, rotation);
      ctx.drawImage(campaignImage, 0, 0, canvas.width, canvas.height);
    } else {
      // Background: User photo ON TOP of background
      ctx.drawImage(campaignImage, 0, 0, canvas.width, canvas.height);
      drawUserPhotoWithAdjustments(ctx, userPhoto, canvas.width, canvas.height, scale, x, y, rotation);
    }

    // Convert to blob for download
    const blob = await canvasToBlob(canvas, 'image/png');

    // Cleanup object URLs
    cleanupImage(userPhoto);
    cleanupImage(campaignImage);

    return { canvas, blob };
  } catch (error) {
    console.error('Image composition error:', error);
    throw error;
  }
};

/**
 * Draw user photo with scale, position, and rotation adjustments
 * @param {CanvasRenderingContext2D} ctx - Canvas context
 * @param {HTMLImageElement} img - User photo image
 * @param {number} canvasWidth - Canvas width
 * @param {number} canvasHeight - Canvas height
 * @param {number} scale - Zoom level (0.1 - 10.0)
 * @param {number} x - Horizontal offset
 * @param {number} y - Vertical offset
 * @param {number} rotation - Rotation angle in degrees (0 - 360)
 */
const drawUserPhotoWithAdjustments = (ctx, img, canvasWidth, canvasHeight, scale, x, y, rotation = 0) => {
  // Save the current context state
  ctx.save();

  // Calculate center point for rotation
  const centerX = canvasWidth / 2;
  const centerY = canvasHeight / 2;

  // Move to center, apply rotation, then move back
  ctx.translate(centerX + x, centerY + y);
  ctx.rotate((rotation * Math.PI) / 180);

  // Calculate scaled dimensions
  const scaledWidth = img.width * scale;
  const scaledHeight = img.height * scale;

  // Draw the image centered at the rotation point
  ctx.drawImage(img, -scaledWidth / 2, -scaledHeight / 2, scaledWidth, scaledHeight);

  // Restore the context state
  ctx.restore();
};

/**
 * Apply adjustments to canvas and return preview
 * Used for real-time preview without full composition
 * @param {HTMLCanvasElement} canvas - Target canvas
 * @param {File} userPhotoFile - User photo file
 * @param {string} campaignImageUrl - Campaign image URL
 * @param {object} adjustments - Adjustments object
 * @param {string} campaignType - Campaign type
 * @returns {Promise<HTMLCanvasElement>} Updated canvas
 */
export const updatePreview = async (canvas, userPhotoFile, campaignImageUrl, adjustments, campaignType) => {
  const ctx = canvas.getContext('2d', { alpha: true });

  if (!ctx) {
    throw new Error('Failed to get canvas context');
  }

  // Clear canvas
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Load and compose images
  const [userPhoto, campaignImage] = await Promise.all([
    loadImage(userPhotoFile),
    loadImage(campaignImageUrl)
  ]);

  // Ensure canvas matches campaign dimensions
  canvas.width = campaignImage.width;
  canvas.height = campaignImage.height;

  const { scale = 1.0, x = 0, y = 0, rotation = 0 } = adjustments;

  // Compose based on type
  if (campaignType === 'frame') {
    drawUserPhotoWithAdjustments(ctx, userPhoto, canvas.width, canvas.height, scale, x, y, rotation);
    ctx.drawImage(campaignImage, 0, 0, canvas.width, canvas.height);
  } else {
    ctx.drawImage(campaignImage, 0, 0, canvas.width, canvas.height);
    drawUserPhotoWithAdjustments(ctx, userPhoto, canvas.width, canvas.height, scale, x, y, rotation);
  }

  // Cleanup
  cleanupImage(userPhoto);
  cleanupImage(campaignImage);

  return canvas;
};

/**
 * Calculate optimal fit for user photo within canvas
 * @param {File} userPhotoFile - User photo file
 * @param {number} canvasWidth - Canvas width
 * @param {number} canvasHeight - Canvas height
 * @returns {Promise<{scale: number, x: number, y: number}>} Optimal adjustments
 */
export const calculateFitAdjustments = async (userPhotoFile, canvasWidth, canvasHeight) => {
  const img = await loadImage(userPhotoFile);

  // Calculate scale to fit image within canvas (contain mode)
  const scaleX = canvasWidth / img.width;
  const scaleY = canvasHeight / img.height;
  const scale = Math.min(scaleX, scaleY);

  // Center the image
  const x = 0;
  const y = 0;

  cleanupImage(img);

  return { scale, x, y };
};

/**
 * Calculate scale to fill canvas (cover mode)
 * @param {File} userPhotoFile - User photo file
 * @param {number} canvasWidth - Canvas width
 * @param {number} canvasHeight - Canvas height
 * @returns {Promise<{scale: number, x: number, y: number}>} Optimal adjustments
 */
export const calculateCoverAdjustments = async (userPhotoFile, canvasWidth, canvasHeight) => {
  const img = await loadImage(userPhotoFile);

  // Calculate scale to cover canvas (may crop image)
  const scaleX = canvasWidth / img.width;
  const scaleY = canvasHeight / img.height;
  const scale = Math.max(scaleX, scaleY);

  // Center the image
  const x = 0;
  const y = 0;

  cleanupImage(img);

  return { scale, x, y };
};

/**
 * Convert canvas to blob for download
 * @param {HTMLCanvasElement} canvas - Canvas element
 * @param {string} format - Image format ('image/png' or 'image/jpeg')
 * @param {number} quality - Image quality (0-1) for JPEG
 * @returns {Promise<Blob>} Image blob
 */
export const canvasToBlob = (canvas, format = 'image/png', quality = 0.95) => {
  return new Promise((resolve, reject) => {
    canvas.toBlob(
      (blob) => {
        if (blob) {
          resolve(blob);
        } else {
          reject(new Error('Failed to create blob from canvas'));
        }
      },
      format,
      quality
    );
  });
};

/**
 * Export canvas as downloadable file
 * @param {HTMLCanvasElement} canvas - Canvas to export
 * @param {string} filename - Download filename
 * @param {string} format - Image format ('png' or 'jpeg')
 */
export const downloadCanvas = async (canvas, filename = 'campaign-image', format = 'png') => {
  const mimeType = format === 'jpeg' ? 'image/jpeg' : 'image/png';
  const blob = await canvasToBlob(canvas, mimeType);

  // Create download link
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = `${filename}.${format}`;

  // Trigger download
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);

  // Cleanup
  URL.revokeObjectURL(url);
};

/**
 * Cleanup object URL from image element
 * @param {HTMLImageElement} img - Image element
 */
const cleanupImage = (img) => {
  if (img.dataset.objectUrl) {
    URL.revokeObjectURL(img.dataset.objectUrl);
    delete img.dataset.objectUrl;
  }
};

/**
 * Get image dimensions from file
 * @param {File} imageFile - Image file
 * @returns {Promise<{width: number, height: number}>} Image dimensions
 */
export const getImageDimensions = async (imageFile) => {
  const img = await loadImage(imageFile);
  const dimensions = {
    width: img.width,
    height: img.height
  };
  cleanupImage(img);
  return dimensions;
};

/**
 * Validate image aspect ratio
 * @param {File} imageFile - Image file
 * @param {number} minRatio - Minimum aspect ratio
 * @param {number} maxRatio - Maximum aspect ratio
 * @returns {Promise<boolean>} True if aspect ratio is valid
 */
export const validateAspectRatio = async (imageFile, minRatio = 0.5, maxRatio = 2.0) => {
  const { width, height } = await getImageDimensions(imageFile);
  const ratio = width / height;
  return ratio >= minRatio && ratio <= maxRatio;
};

src/app/(chrome)/campaign/[slug]/page.js.content:
"use client";

import { useState, useRef, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { getCampaignBySlug, createReport } from '../../../../lib/firestore';
import { useCampaignSession } from '../../../../contexts/CampaignSessionContext';
import { useAuth } from '../../../../hooks/useAuth';
import LoadingSpinner from '../../../../components/LoadingSpinner';
import CampaignStepIndicator from '../../../../components/CampaignStepIndicator';

export default function CampaignUploadPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useAuth();
  const slug = params.slug;
  const campaignSession = useCampaignSession();

  // State
  const [campaign, setCampaign] = useState(null);
  const [creator, setCreator] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notFound, setNotFound] = useState(false);
  const [uploading, setUploading] = useState(false);

  // Report modal state
  const [showReportModal, setShowReportModal] = useState(false);
  const [reportReason, setReportReason] = useState('');
  const [reportDetails, setReportDetails] = useState('');
  const [reportSubmitting, setReportSubmitting] = useState(false);
  const [error, setError] = useState('');

  const fileInputRef = useRef();

  // Fetch campaign data
  useEffect(() => {
    const fetchCampaign = async () => {
      if (!slug) return;

      setLoading(true);
      try {
        const result = await getCampaignBySlug(slug);

        if (!result) {
          setNotFound(true);
          setLoading(false);
          return;
        }

        setCampaign(result.campaign);
        setCreator(result.creator);

        // Store campaign and creator data in session
        campaignSession.setCampaignData(slug, result.campaign);
        campaignSession.setCreatorData(slug, result.creator);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching campaign:', error);
        setNotFound(true);
        setLoading(false);
      }
    };

    fetchCampaign();
  }, [slug]);

  // Handle photo selection
  const handlePhotoSelect = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setError('');
    setUploading(true);

    // File size validation (10MB limit)
    const maxSize = 10 * 1024 * 1024;
    if (file.size > maxSize) {
      setError(`Photo must be smaller than 10MB. Current size: ${(file.size / (1024 * 1024)).toFixed(1)}MB`);
      setUploading(false);
      return;
    }

    // File type validation
    const validTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/webp'];
    if (!validTypes.includes(file.type)) {
      setError('Please upload a PNG, JPG, or WEBP image');
      setUploading(false);
      return;
    }

    // Create preview
    const reader = new FileReader();
    reader.onload = async (event) => {
      // Store in session
      campaignSession.setUserPhoto(slug, file, event.target.result);

      // Initialize adjustments
      campaignSession.setAdjustments(slug, { scale: 1.0, x: 0, y: 0 });

      // Redirect to adjust page
      router.push(`/campaign/${slug}/adjust`);
    };

    reader.onerror = () => {
      setError('Failed to read image file');
      setUploading(false);
    };

    reader.readAsDataURL(file);
  };

  // Report campaign
  const handleReportSubmit = async (e) => {
    e.preventDefault();

    if (!reportReason) {
      setError('Please select a reason for reporting');
      return;
    }

    setReportSubmitting(true);
    setError('');

    try {
      const reportData = {
        campaignId: campaign.id,
        campaignSlug: campaign.slug,
        reportedBy: user?.uid || 'anonymous',
        reason: reportReason,
        details: reportDetails
      };

      const result = await createReport(reportData);

      if (result.success) {
        setShowReportModal(false);
        setReportReason('');
        setReportDetails('');
        alert('Thank you for your report. We will review it shortly.');
      } else {
        setError(result.error || 'Failed to submit report');
      }

      setReportSubmitting(false);
    } catch (error) {
      console.error('Error submitting report:', error);
      setError('Failed to submit report. Please try again.');
      setReportSubmitting(false);
    }
  };

  // Share functionality
  const handleShare = async (platform) => {
    const url = window.location.href;
    const text = campaign.title + (campaign.description ? ` - ${campaign.description}` : '');

    switch (platform) {
      case 'twitter':
        window.open(`https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(url)}`, '_blank');
        break;
      case 'facebook':
        window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`, '_blank');
        break;
      case 'whatsapp':
        window.open(`https://wa.me/?text=${encodeURIComponent(text + ' ' + url)}`, '_blank');
        break;
      default:
        break;
    }
  };

  // Loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  // Not found state
  if (notFound || !campaign) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center px-4">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Campaign Not Found</h1>
          <p className="text-gray-600 mb-8">This campaign doesn't exist or has been removed.</p>
          <button
            onClick={() => router.push('/')}
            className="btn-base btn-primary px-6 py-3 font-medium"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <div className="min-h-screen flex">
        {/* Main Content */}
        <div className="flex-1 w-full flex flex-col py-8 px-4 sm:px-6 lg:px-8 pt-20">
          <div className="mx-auto w-full max-w-5xl">

            {/* Header */}
            <div className="text-center mb-6 bg-yellow-400 px-6 py-5 rounded-t-xl">
              <CampaignStepIndicator currentStep={1} />
              <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-1">{campaign.title}</h1>
              {campaign.description && (
                <p className="text-sm text-gray-800 mt-1">{campaign.description}</p>
              )}
            </div>

            {/* Content Card */}
            <div className="bg-white rounded-b-xl border border-t-0 border-gray-200 shadow-sm">

              {error && (
                <div className="mx-4 mt-4 p-4 bg-red-50 border border-red-200 rounded-lg text-red-600 text-sm text-center">
                  {error}
                </div>
              )}

              <div className="grid grid-cols-1 lg:grid-cols-[minmax(0,1.1fr)_minmax(320px,0.9fr)] gap-6 p-4 sm:p-6">

                {/* Left: Campaign Preview */}
                <div className="space-y-3">
                  <h2 className="text-lg font-bold text-gray-900">Campaign Preview</h2>

                  <div className="relative">
                    <img
                      src={campaign.imageUrl}
                      alt={campaign.title}
                      className="w-full h-auto rounded-lg border-2 border-gray-300"
                      style={{ maxHeight: '500px', objectFit: 'contain' }}
                    />
                    <div className="absolute top-4 right-4 bg-emerald-500 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase">
                      {campaign.type === 'frame' ? 'Frame' : 'Background'}
                    </div>
                  </div>

                  {creator && (
                    <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg border border-gray-200">
                      {creator.profileImage && (
                        <img
                          src={creator.profileImage}
                          alt={creator.displayName}
                          className="w-10 h-10 rounded-full border-2 border-gray-300"
                        />
                      )}
                      <div className="flex-1">
                        <p className="text-sm text-gray-600">Created by</p>
                        <button
                          onClick={() => router.push(`/u/${creator.username}`)}
                          className="font-semibold text-gray-900 hover:underline text-sm"
                        >
                          {creator.displayName || creator.username}
                        </button>
                        <p className="text-xs text-gray-600">
                          {campaign.supportersCount || 0} {campaign.supportersCount === 1 ? 'support' : 'supports'}
                        </p>
                      </div>
                    </div>
                  )}
                </div>

                {/* Right: Upload & Actions */}
                <div className="space-y-5">

                  {/* Upload Section */}
                  <div>
                    <h2 className="text-lg font-bold text-gray-900 mb-3">Add Your Photo</h2>
                    <p className="text-sm text-gray-600 mb-4">
                      Upload your photo to create your personalized {campaign.type === 'frame' ? 'frame' : 'background'}
                    </p>

                    <input
                      ref={fileInputRef}
                      type="file"
                      accept="image/png,image/jpeg,image/jpg,image/webp"
                      onChange={handlePhotoSelect}
                      disabled={uploading}
                      className="hidden"
                      id="user-photo-input"
                    />

                    <label
                      htmlFor="user-photo-input"
                      className={`btn-base btn-primary w-full text-center py-4 cursor-pointer font-bold text-lg ${
                        uploading ? 'opacity-70 cursor-wait' : ''
                      }`}
                    >
                      {uploading ? 'Loading...' : 'Choose Your Photo'}
                    </label>

                    <p className="text-xs text-gray-600 mt-2 text-center">
                      PNG, JPG, or WEBP (max 10MB)
                    </p>
                  </div>

                  {/* Share Campaign */}
                  <div className="pt-3 border-t border-gray-200">
                    <h3 className="text-lg font-bold text-gray-900 mb-3">Share Campaign</h3>
                    <div className="grid grid-cols-3 gap-3">
                      <button
                        onClick={() => handleShare('twitter')}
                        className="btn-base bg-blue-400 hover:bg-blue-500 text-white py-3 text-sm font-medium"
                      >
                        Twitter
                      </button>
                      <button
                        onClick={() => handleShare('facebook')}
                        className="btn-base bg-blue-600 hover:bg-blue-700 text-white py-3 text-sm font-medium"
                      >
                        Facebook
                      </button>
                      <button
                        onClick={() => handleShare('whatsapp')}
                        className="btn-base bg-green-500 hover:bg-green-600 text-white py-3 text-sm font-medium"
                      >
                        WhatsApp
                      </button>
                    </div>
                  </div>

                  {/* Report Button */}
                  <button
                    onClick={() => setShowReportModal(true)}
                    className="btn-base bg-red-100 hover:bg-red-200 text-red-700 w-full py-2 text-sm font-medium"
                  >
                    Report Campaign
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Report Modal */}
      {showReportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Report Campaign</h2>

            <form onSubmit={handleReportSubmit}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Reason <span className="text-red-500">*</span>
                </label>
                <select
                  value={reportReason}
                  onChange={(e) => setReportReason(e.target.value)}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500 outline-none transition-all"
                >
                  <option value="">Select a reason</option>
                  <option value="inappropriate">Inappropriate Content</option>
                  <option value="spam">Spam</option>
                  <option value="copyright">Copyright Violation</option>
                  <option value="other">Other</option>
                </select>
              </div>

              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Additional Details (Optional)
                </label>
                <textarea
                  value={reportDetails}
                  onChange={(e) => setReportDetails(e.target.value)}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500 outline-none transition-all resize-none"
                  placeholder="Provide more context about your report..."
                />
              </div>

              <div className="flex gap-3">
                <button
                  type="button"
                  onClick={() => {
                    setShowReportModal(false);
                    setReportReason('');
                    setReportDetails('');
                    setError('');
                  }}
                  className="btn-base btn-secondary flex-1 py-2 font-medium"
                  disabled={reportSubmitting}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn-base bg-red-500 hover:bg-red-600 text-white flex-1 py-2 font-medium"
                  disabled={reportSubmitting || !reportReason}
                >
                  {reportSubmitting ? 'Submitting...' : 'Submit Report'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

