    country.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Move selected country to top of filtered list
  const sortedCountries = [
    ...filteredCountries.filter(country => country === selectedCountry),
    ...filteredCountries.filter(country => country !== selectedCountry)
